/**
 * æ£€æŸ¥ç”¨æˆ·æ˜¯å¦å…·æœ‰æŒ‡å®šè§’è‰²
 * @param {Array|string} allowedRoles - å…è®¸çš„è§’è‰²åˆ—è¡¨
 * @returns {Function} Expressä¸­é—´ä»¶å‡½æ•°
 */
function requireRole(allowedRoles) {
  // ç¡®ä¿allowedRolesæ˜¯æ•°ç»„
  const roles = Array.isArray(allowedRoles) ? allowedRoles : [allowedRoles];

  return (req, res, next) => {
    try {
      // æ£€æŸ¥ç”¨æˆ·æ˜¯å¦å·²è®¤è¯
      if (!req.user) {
        return responseFormatter.unauthorized(res, 'ç”¨æˆ·æœªè®¤è¯');
      }

      const userRole = req.user.role;

      // æ£€æŸ¥ç”¨æˆ·è§’è‰²æ˜¯å¦åœ¨å…è®¸çš„è§’è‰²åˆ—è¡¨ä¸­
      if (!roles.includes(userRole)) {
        return responseFormatter.forbidden(res, `éœ€è¦ä»¥ä¸‹è§’è‰²ä¹‹ä¸€: ${roles.join(', ')}`);
      }

      // è®°å½•æƒé™æ£€æŸ¥æ—¥å¿—
      console.log(
        `[${new Date().toISOString()}] Role Check: User ${req.user.username} (${userRole}) accessing ${req.method} ${req.path}`
      );

      next();
    } catch (error) {
      console.error('è§’è‰²æ£€æŸ¥é”™è¯¯:', error);
      return responseFormatter.internalError(res, 'æƒé™éªŒè¯å¤±è´¥', error);
    }
  };
}

/**
 * æ£€æŸ¥ç”¨æˆ·æ˜¯å¦å…·æœ‰æŒ‡å®šæƒé™
 * @param {Array|string} requiredPermissions - éœ€è¦çš„æƒé™åˆ—è¡¨
 * @returns {Function} Expressä¸­é—´ä»¶å‡½æ•°
 */
function requirePermission(requiredPermissions) {
  // ç¡®ä¿requiredPermissionsæ˜¯æ•°ç»„
  const permissions = Array.isArray(requiredPermissions)
    ? requiredPermissions
    : [requiredPermissions];

  return (req, res, next) => {
    try {
      // æ£€æŸ¥ç”¨æˆ·æ˜¯å¦å·²è®¤è¯
      if (!req.user) {
        return responseFormatter.unauthorized(res, 'ç”¨æˆ·æœªè®¤è¯');
      }

      const userRole = req.user.role;
      const userPermissions = ROLE_PERMISSIONS[userRole] || [];

      // æ£€æŸ¥ç”¨æˆ·æ˜¯å¦å…·æœ‰æ‰€æœ‰éœ€è¦çš„æƒé™
      const hasAllPermissions = permissions.every((permission) =>
        userPermissions.includes(permission)
      );

      if (!hasAllPermissions) {
        const missingPermissions = permissions.filter(
          (permission) => !userPermissions.includes(permission)
        );

        return responseFormatter.forbidden(res, `ç¼ºå°‘æƒé™: ${missingPermissions.join(', ')}`);
      }

      // è®°å½•æƒé™æ£€æŸ¥æ—¥å¿—
      console.log(
        `[${new Date().toISOString()}] Permission Check: User ${req.user.username} (${userRole}) has permissions: ${permissions.join(', ')}`
      );

      next();
    } catch (error) {
      console.error('æƒé™æ£€æŸ¥é”™è¯¯:', error);
      return responseFormatter.internalError(res, 'æƒé™éªŒè¯å¤±è´¥', error);
    }
  };
}

/**
 * æ£€æŸ¥ç”¨æˆ·æ˜¯å¦å…·æœ‰è¶³å¤Ÿçš„è§’è‰²çº§åˆ«
 * @param {string} minimumRole - æœ€ä½è¦æ±‚çš„è§’è‰²
 * @returns {Function} Expressä¸­é—´ä»¶å‡½æ•°
 */
function requireMinimumRole(minimumRole) {
  return (req, res, next) => {
    try {
      // æ£€æŸ¥ç”¨æˆ·æ˜¯å¦å·²è®¤è¯
      if (!req.user) {
        return responseFormatter.unauthorized(res, 'ç”¨æˆ·æœªè®¤è¯');
      }

      const userRole = req.user.role;
      const userLevel = ROLE_LEVELS[userRole] || 0;
      const requiredLevel = ROLE_LEVELS[minimumRole] || 0;

      if (userLevel < requiredLevel) {
        return responseFormatter.forbidden(res, `éœ€è¦è‡³å°‘ ${minimumRole} è§’è‰²æƒé™`);
      }

      // è®°å½•æƒé™æ£€æŸ¥æ—¥å¿—
      console.log(
        `[${new Date().toISOString()}] Role Level Check: User ${req.user.username} (${userRole}:${userLevel}) meets minimum requirement (${minimumRole}:${requiredLevel})`
      );

      next();
    } catch (error) {
      console.error('è§’è‰²çº§åˆ«æ£€æŸ¥é”™è¯¯:', error);
      return responseFormatter.internalError(res, 'æƒé™éªŒè¯å¤±è´¥', error);
    }
  };
}

/**
 * æ£€æŸ¥ç”¨æˆ·æ˜¯å¦ä¸ºèµ„æºæ‰€æœ‰è€…æˆ–å…·æœ‰ç®¡ç†æƒé™
 * @param {Function} getResourceOwnerId - è·å–èµ„æºæ‰€æœ‰è€…IDçš„å‡½æ•°
 * @returns {Function} Expressä¸­é—´ä»¶å‡½æ•°
 */
function requireOwnershipOrAdmin(getResourceOwnerId) {
  return async (req, res, next) => {
    try {
      // æ£€æŸ¥ç”¨æˆ·æ˜¯å¦å·²è®¤è¯
      if (!req.user) {
        return responseFormatter.unauthorized(res, 'ç”¨æˆ·æœªè®¤è¯');
      }

      const userRole = req.user.role;
      const {userId} = req.user;

      // ç®¡ç†å‘˜å¯ä»¥è®¿é—®æ‰€æœ‰èµ„æº
      if (userRole === 'admin') {
        return next();
      }

      // è·å–èµ„æºæ‰€æœ‰è€…ID
      const resourceOwnerId = await getResourceOwnerId(req);

      // æ£€æŸ¥ç”¨æˆ·æ˜¯å¦ä¸ºèµ„æºæ‰€æœ‰è€…
      if (userId !== resourceOwnerId) {
        return responseFormatter.forbidden(res, 'åªèƒ½è®¿é—®è‡ªå·±çš„èµ„æº');
      }

      // è®°å½•æƒé™æ£€æŸ¥æ—¥å¿—
      console.log(
        `[${new Date().toISOString()}] Ownership Check: User ${req.user.username} accessing own resource`
      );

      next();
    } catch (error) {
      console.error('æ‰€æœ‰æƒæ£€æŸ¥é”™è¯¯:', error);
      return responseFormatter.internalError(res, 'æƒé™éªŒè¯å¤±è´¥', error);
    }
  };
}

/**
 * æ¡ä»¶æƒé™æ£€æŸ¥
 * @param {Function} condition - æ¡ä»¶æ£€æŸ¥å‡½æ•°
 * @param {Array|string} allowedRoles - æ»¡è¶³æ¡ä»¶æ—¶å…è®¸çš„è§’è‰²
 * @param {Array|string} fallbackRoles - ä¸æ»¡è¶³æ¡ä»¶æ—¶å…è®¸çš„è§’è‰²
 * @returns {Function} Expressä¸­é—´ä»¶å‡½æ•°
 */
function conditionalRole(condition, allowedRoles, fallbackRoles = []) {
  return async (req, res, next) => {
    try {
      // æ£€æŸ¥ç”¨æˆ·æ˜¯å¦å·²è®¤è¯
      if (!req.user) {
        return responseFormatter.unauthorized(res, 'ç”¨æˆ·æœªè®¤è¯');
      }

      const userRole = req.user.role;
      const conditionMet = await condition(req);

      const targetRoles = conditionMet
        ? Array.isArray(allowedRoles)
          ? allowedRoles
          : [allowedRoles]
        : Array.isArray(fallbackRoles)
          ? fallbackRoles
          : [fallbackRoles];

      if (!targetRoles.includes(userRole)) {
        return responseFormatter.forbidden(
          res,
          `å½“å‰æ¡ä»¶ä¸‹éœ€è¦ä»¥ä¸‹è§’è‰²ä¹‹ä¸€: ${targetRoles.join(', ')}`
        );
      }

      // è®°å½•æƒé™æ£€æŸ¥æ—¥å¿—
      console.log(
        `[${new Date().toISOString()}] Conditional Role Check: User ${req.user.username} (${userRole}) - condition: ${conditionMet}`
      );

      next();
    } catch (error) {
      console.error('æ¡ä»¶æƒé™æ£€æŸ¥é”™è¯¯:', error);
      return responseFormatter.internalError(res, 'æƒé™éªŒè¯å¤±è´¥', error);
    }
  };
}

/**
 * è·å–ç”¨æˆ·æƒé™åˆ—è¡¨
 * @param {string} role - ç”¨æˆ·è§’è‰²
 * @returns {Array} æƒé™åˆ—è¡¨
 */
function getUserPermissions(role) {
  return ROLE_PERMISSIONS[role] || [];
}

/**
 * æ£€æŸ¥è§’è‰²æ˜¯å¦å…·æœ‰æŒ‡å®šæƒé™
 * @param {string} role - è§’è‰²
 * @param {string} permission - æƒé™
 * @returns {boolean} æ˜¯å¦å…·æœ‰æƒé™
 */
function hasPermission(role, permission) {
  const permissions = ROLE_PERMISSIONS[role] || [];
  return permissions.includes(permission);
}

/**
 * è·å–è§’è‰²çº§åˆ«
 * @param {string} role - è§’è‰²
 * @returns {number} è§’è‰²çº§åˆ«
 */
function getRoleLevel(role) {
  return ROLE_LEVELS[role] || 0;
}

/**
 * æ¯”è¾ƒä¸¤ä¸ªè§’è‰²çš„çº§åˆ«
 * @param {string} role1 - è§’è‰²1
 * @param {string} role2 - è§’è‰²2
 * @returns {number} æ¯”è¾ƒç»“æœ (1: role1 > role2, 0: ç›¸ç­‰, -1: role1 < role2)
 */
function compareRoles(role1, role2) {
  const level1 = getRoleLevel(role1);
  const level2 = getRoleLevel(role2);

  if (level1 > level2) {return 1;}
  if (level1 < level2) {return -1;}
  return 0;
}

/**
 * æƒé™æ£€æŸ¥ä¸­é—´ä»¶å·¥å‚
 * æ ¹æ®HTTPæ–¹æ³•è‡ªåŠ¨é€‰æ‹©æƒé™æ£€æŸ¥ç­–ç•¥
 * @param {string} resource - èµ„æºåç§°
 * @returns {Function} Expressä¸­é—´ä»¶å‡½æ•°
 */
function autoPermissionCheck(resource) {
  return (req, res, next) => {
    const method = req.method.toLowerCase();
    let requiredPermission;

    switch (method) {
      case 'get':
        requiredPermission = `${resource}:read`;
        break;
      case 'post':
        requiredPermission = `${resource}:create`;
        break;
      case 'put':
      case 'patch':
        requiredPermission = `${resource}:update`;
        break;
      case 'delete':
        requiredPermission = `${resource}:delete`;
        break;
      default:
        return responseFormatter.error(res, 'ä¸æ”¯æŒçš„HTTPæ–¹æ³•', 405);
    }

    return requirePermission(requiredPermission)(req, res, next);
  };
}

