/**
 * æ–‡ä»¶ä¸Šä¼ éªŒè¯
 */
export const validateFileUpload =
  (allowedTypes = [], maxSize = 10 * 1024 * 1024) =>
    (req, res, next) => {
      if (!req.file && !req.files) {
        return next(new ValidationError('æœªæ‰¾åˆ°ä¸Šä¼ çš„æ–‡ä»¶'));
      }

      const files = req.files || [req.file];

      for (const file of files) {
      // æ£€æŸ¥æ–‡ä»¶ç±»å‹
        if (allowedTypes.length > 0 && !allowedTypes.includes(file.mimetype)) {
          return next(
            new ValidationError(
              `ä¸æ”¯æŒçš„æ–‡ä»¶ç±»å‹: ${file.mimetype}ï¼Œæ”¯æŒçš„ç±»å‹: ${allowedTypes.join(', ')}`
            )
          );
        }

        // æ£€æŸ¥æ–‡ä»¶å¤§å°
        if (file.size > maxSize) {
          return next(
            new ValidationError(`æ–‡ä»¶å¤§å°è¶…è¿‡é™åˆ¶: ${file.size} bytesï¼Œæœ€å¤§å…è®¸: ${maxSize} bytes`)
          );
        }

        // æ£€æŸ¥æ–‡ä»¶å
        if (!/^[a-zA-Z0-9._-]+$/.test(file.originalname)) {
          return next(new ValidationError('æ–‡ä»¶ååŒ…å«éæ³•å­—ç¬¦'));
        }
      }

      next();
    };

/**
