// å¸¸ç”¨éªŒè¯è§„åˆ™
const VALIDATION_RULES = {
  // IDéªŒè¯
  id: param('id').isInt({ min: 1 }).withMessage('IDå¿…é¡»æ˜¯æ­£æ•´æ•°'),

  // UUIDéªŒè¯
  uuid: param('id').isUUID().withMessage('IDå¿…é¡»æ˜¯æœ‰æ•ˆçš„UUID'),

  // åˆ†é¡µå‚æ•°éªŒè¯
  pagination: [
    query('page').optional().isInt({ min: 1 }).withMessage('é¡µç å¿…é¡»æ˜¯æ­£æ•´æ•°').toInt(),
    query('limit')
      .optional()
      .isInt({ min: 1, max: 100 })
      .withMessage('æ¯é¡µæ•°é‡å¿…é¡»åœ¨1-100ä¹‹é—´')
      .toInt(),
    query('offset').optional().isInt({ min: 0 }).withMessage('åç§»é‡å¿…é¡»æ˜¯éè´Ÿæ•´æ•°').toInt()
  ],

  // æ’åºå‚æ•°éªŒè¯
  sorting: [
    query('sortBy')
