    query('sortOrder')
      .optional()
      .isIn(['asc', 'desc', 'ASC', 'DESC'])
      .withMessage('æ’åºæ–¹å‘å¿…é¡»æ˜¯ascæˆ–desc')
  ],

  // æ—¥æœŸèŒƒå›´éªŒè¯
  dateRange: [
    query('startDate').optional().isISO8601().withMessage('å¼€å§‹æ—¥æœŸæ ¼å¼æ— æ•ˆ').toDate(),
    query('endDate')
      .optional()
      .isISO8601()
      .withMessage('ç»“æŸæ—¥æœŸæ ¼å¼æ— æ•ˆ')
      .toDate()
      .custom((value, { req }) => {
        if (req.query.startDate && value < new Date(req.query.startDate)) {
          throw new Error('ç»“æŸæ—¥æœŸä¸èƒ½æ—©äºå¼€å§‹æ—¥æœŸ');
        }
        return true;
      })
  ],

  // æœç´¢å‚æ•°éªŒè¯
  search: [
    query('keyword')
      .optional()
      .isString()
      .isLength({ min: 1, max: 100 })
      .withMessage('æœç´¢å…³é”®è¯é•¿åº¦å¿…é¡»åœ¨1-100ä¹‹é—´')
      .trim()
      .escape(),
    query('searchFields').optional().isString().withMessage('æœç´¢å­—æ®µå¿…é¡»æ˜¯å­—ç¬¦ä¸²')
  ],

  // ç”¨æˆ·åéªŒè¯
  username: body('username')
