    .withMessage('ç”¨æˆ·åå¿…é¡»æ˜¯3-20ä½å­—æ¯ã€æ•°å­—æˆ–ä¸‹åˆ’çº¿'),

  // å¯†ç éªŒè¯
  password: body('password')
    .matches(REGEX_PATTERNS.PASSWORD)
    .withMessage('å¯†ç è‡³å°‘8ä½ï¼Œå¿…é¡»åŒ…å«å­—æ¯å’Œæ•°å­—'),

  // é‚®ç®±éªŒè¯
  email: body('email').isEmail().normalizeEmail().withMessage('é‚®ç®±æ ¼å¼æ— æ•ˆ'),

  // æ‰‹æœºå·éªŒè¯
  phone: body('phone').optional().matches(REGEX_PATTERNS.PHONE).withMessage('æ‰‹æœºå·æ ¼å¼æ— æ•ˆ'),

  // è§’è‰²éªŒè¯
  role: body('role')
    .isIn(['admin', 'operator', 'user', 'guest'])
    .withMessage('è§’è‰²å¿…é¡»æ˜¯adminã€operatorã€useræˆ–guestä¹‹ä¸€'),

  // çŠ¶æ€éªŒè¯
  status: body('status')
    .isIn(['active', 'inactive', 'pending', 'suspended'])
    .withMessage('çŠ¶æ€å¿…é¡»æ˜¯activeã€inactiveã€pendingæˆ–suspendedä¹‹ä¸€'),

  // å¸ƒå°”å€¼éªŒè¯
  boolean: (field) => body(field).isBoolean().withMessage(`${field}å¿…é¡»æ˜¯å¸ƒå°”å€¼`).toBoolean(),

  // æ•°å­—éªŒè¯
  number: (field, min = 0, max = Number.MAX_SAFE_INTEGER) =>
    body(field)
      .isNumeric()
      .withMessage(`${field}å¿…é¡»æ˜¯æ•°å­—`)
      .isFloat({ min, max })
      .withMessage(`${field}å¿…é¡»åœ¨${min}-${max}ä¹‹é—´`)
      .toFloat(),

  // æ•´æ•°éªŒè¯
  integer: (field, min = 0, max = Number.MAX_SAFE_INTEGER) =>
    body(field).isInt({ min, max }).withMessage(`${field}å¿…é¡»æ˜¯${min}-${max}ä¹‹é—´çš„æ•´æ•°`).toInt(),

  // å­—ç¬¦ä¸²éªŒè¯
  string: (field, minLength = 1, maxLength = 255) =>
