      .isString()
      .withMessage(`${field}å¿…é¡»æ˜¯å­—ç¬¦ä¸²`)
      .isLength({ min: minLength, max: maxLength })
      .withMessage(`${field}é•¿åº¦å¿…é¡»åœ¨${minLength}-${maxLength}ä¹‹é—´`)
      .trim(),

  // æ•°ç»„éªŒè¯
  array: (field, minLength = 0, maxLength = 100) =>
    body(field)
      .isArray({ min: minLength, max: maxLength })
      .withMessage(`${field}å¿…é¡»æ˜¯åŒ…å«${minLength}-${maxLength}ä¸ªå…ƒç´ çš„æ•°ç»„`),

  // JSONéªŒè¯
  json: (field) =>
