// è‡ªå®šä¹‰éªŒè¯å™¨
const CUSTOM_VALIDATORS = {
  // éªŒè¯è®¾å¤‡åºåˆ—å·
  deviceSerial: body('serialNumber')
    .matches(REGEX_PATTERNS.DEVICE_SERIAL)
    .withMessage('è®¾å¤‡åºåˆ—å·æ ¼å¼æ— æ•ˆ'),

  // éªŒè¯IPåœ°å€
  ipAddress: (field) =>
    body(field).matches(REGEX_PATTERNS.IPV4).withMessage(`${field}å¿…é¡»æ˜¯æœ‰æ•ˆçš„IPv4åœ°å€`),

  // éªŒè¯MACåœ°å€
  macAddress: (field) =>
    body(field).matches(REGEX_PATTERNS.MAC).withMessage(`${field}å¿…é¡»æ˜¯æœ‰æ•ˆçš„MACåœ°å€`),

  // éªŒè¯ç‰ˆæœ¬å·
  version: (field) =>
    body(field)
      .matches(REGEX_PATTERNS.VERSION)
      .withMessage(`${field}å¿…é¡»æ˜¯æœ‰æ•ˆçš„ç‰ˆæœ¬å·æ ¼å¼ï¼ˆå¦‚1.0.0ï¼‰`),

  // éªŒè¯èƒ½è€—å€¼
  energyValue: body('value').isFloat({ min: 0 }).withMessage('èƒ½è€—å€¼å¿…é¡»æ˜¯éè´Ÿæ•°').toFloat(),

  // éªŒè¯ç¢³æ’æ”¾å› å­
  carbonFactor: body('factor')
    .isFloat({ min: 0, max: 10 })
    .withMessage('ç¢³æ’æ”¾å› å­å¿…é¡»åœ¨0-10ä¹‹é—´')
    .toFloat(),

  // éªŒè¯æ¸©åº¦å€¼
  temperature: (field) =>
    body(field)
      .isFloat({ min: -273.15, max: 1000 })
      .withMessage(`${field}å¿…é¡»æ˜¯æœ‰æ•ˆçš„æ¸©åº¦å€¼ï¼ˆ-273.15Â°Cåˆ°1000Â°Cï¼‰`)
      .toFloat(),

  // éªŒè¯æ¹¿åº¦å€¼
  humidity: (field) =>
    body(field)
      .isFloat({ min: 0, max: 100 })
      .withMessage(`${field}å¿…é¡»æ˜¯0-100ä¹‹é—´çš„æ¹¿åº¦å€¼`)
      .toFloat(),

  // éªŒè¯åŠŸç‡å€¼
  power: (field) =>
    body(field).isFloat({ min: 0 }).withMessage(`${field}å¿…é¡»æ˜¯éè´Ÿçš„åŠŸç‡å€¼`).toFloat(),

  // éªŒè¯ç»çº¬åº¦
  latitude: (field) =>
    body(field)
      .isFloat({ min: -90, max: 90 })
      .withMessage(`${field}å¿…é¡»æ˜¯æœ‰æ•ˆçš„çº¬åº¦å€¼ï¼ˆ-90åˆ°90ï¼‰`)
      .toFloat(),

  longitude: (field) =>
    body(field)
      .isFloat({ min: -180, max: 180 })
      .withMessage(`${field}å¿…é¡»æ˜¯æœ‰æ•ˆçš„ç»åº¦å€¼ï¼ˆ-180åˆ°180ï¼‰`)
      .toFloat(),

  // éªŒè¯æ—¶é—´æˆ³
  timestamp: (field) =>
    body(field).custom((value) => {
      const timestamp = new Date(value).getTime();
      if (isNaN(timestamp)) {
        throw new Error(`${field}å¿…é¡»æ˜¯æœ‰æ•ˆçš„æ—¶é—´æˆ³`);
      }
      // æ£€æŸ¥æ—¶é—´æˆ³æ˜¯å¦åœ¨åˆç†èŒƒå›´å†…ï¼ˆ1970å¹´åˆ°2100å¹´ï¼‰
      if (timestamp < 0 || timestamp > 4102444800000) {
        throw new Error(`${field}æ—¶é—´æˆ³è¶…å‡ºæœ‰æ•ˆèŒƒå›´`);
      }
      return true;
    }),

  // éªŒè¯æ–‡ä»¶å¤§å°ï¼ˆå­—èŠ‚ï¼‰
  fileSize: (field, maxSize = 10 * 1024 * 1024) =>
