/**
 * éªŒè¯ç»“æœå¤„ç†ä¸­é—´ä»¶
 * æ£€æŸ¥éªŒè¯ç»“æœï¼Œå¦‚æœæœ‰é”™è¯¯åˆ™è¿”å›é”™è¯¯å“åº”
 */
function handleValidationErrors(req, res, next) {
  const errors = validationResult(req);

  if (!errors.isEmpty()) {
    const formattedErrors = errors.array().map((error) => ({
      field: error.param,
      message: error.msg,
      value: error.value,
      location: error.location
    }));

    return responseFormatter.validationError(res, 'æ•°æ®éªŒè¯å¤±è´¥', formattedErrors);
  }

  next();
}

/**
 * åˆ›å»ºéªŒè¯é“¾
 * @param {Array} validators - éªŒè¯å™¨æ•°ç»„
 * @returns {Array} åŒ…å«éªŒè¯å™¨å’Œé”™è¯¯å¤„ç†çš„å®Œæ•´éªŒè¯é“¾
 */
function createValidationChain(validators) {
  return [...validators, handleValidationErrors];
}

/**
 * æ¡ä»¶éªŒè¯
 * @param {Function} condition - æ¡ä»¶å‡½æ•°
 * @param {Array} validators - å½“æ¡ä»¶ä¸ºçœŸæ—¶åº”ç”¨çš„éªŒè¯å™¨
 * @returns {Function} æ¡ä»¶éªŒè¯ä¸­é—´ä»¶
 */
function conditionalValidation(condition, validators) {
  const runNext = function(req, res, next, validationChain, index) {
    if (index >= validationChain.length) {
      return next();
    }

    const validator = validationChain[index];
    validator(req, res, () => runNext(req, res, next, validationChain, index + 1));
  };

  return (req, res, next) => {
    if (condition(req)) {
      // åº”ç”¨éªŒè¯å™¨
      const validationChain = createValidationChain(validators);
      runNext(req, res, next, validationChain, 0);
    } else {
      next();
    }
  };
}

/**
 * æ‰¹é‡éªŒè¯
 * @param {Object} validationRules - éªŒè¯è§„åˆ™å¯¹è±¡
 * @returns {Array} éªŒè¯å™¨æ•°ç»„
 */
function batchValidation(validationRules) {
  const validators = [];

  for (const [_field, rules] of Object.entries(validationRules)) {
    if (Array.isArray(rules)) {
      validators.push(...rules);
    } else {
      validators.push(rules);
    }
  }

  return createValidationChain(validators);
}

/**
 * è‡ªå®šä¹‰éªŒè¯å™¨å·¥å‚
 * @param {Function} validatorFn - éªŒè¯å‡½æ•°
 * @param {string} errorMessage - é”™è¯¯æ¶ˆæ¯
 * @returns {Function} è‡ªå®šä¹‰éªŒè¯å™¨
 */
function customValidator(validatorFn, errorMessage) {
  return (field) => body(field).custom(validatorFn).withMessage(errorMessage);
}

module.exports = {
  REGEX_PATTERNS,
  VALIDATION_RULES,
  CUSTOM_VALIDATORS,
  handleValidationErrors,
  createValidationChain,
  conditionalValidation,
  batchValidation,
  customValidator
};

