/**
 * ç¼“å­˜ç®¡ç†å™¨
 * æ”¯æŒå†…å­˜ç¼“å­˜å’ŒRedisç¼“å­˜
 */
class CacheManager {
  constructor() {
    this.memoryCache = new Map();
    this.redisClient = null;
    this.defaultTTL = 3600; // é»˜è®¤1å°æ—¶
    this.maxMemoryItems = 1000; // å†…å­˜ç¼“å­˜æœ€å¤§æ¡ç›®æ•°

    this.initializeRedis();
  }

  /**
   * åˆå§‹åŒ–Redisè¿æ¥
   */
  async initializeRedis() {
    try {
      // å°è¯•å¯¼å…¥Rediså®¢æˆ·ç«¯
      const redisClient = await import('../../database/redisClient.js');
      this.redisClient = redisClient.default;
      logger.info('Redisç¼“å­˜å·²å¯ç”¨');
    } catch (error) {
      logger.warn('Redisä¸å¯ç”¨ï¼Œä½¿ç”¨å†…å­˜ç¼“å­˜', { error: error.message });
    }
  }

  /**
   * è·å–ç¼“å­˜å€¼
   * @param {string} key - ç¼“å­˜é”®
   * @returns {Promise<any>} ç¼“å­˜å€¼
   */
  async get(key) {
    try {
      // ä¼˜å…ˆä½¿ç”¨Redis
      if (this.redisClient) {
        const value = await this.redisClient.get(key);
        if (value !== null) {
          return JSON.parse(value);
        }
      }

      // å›é€€åˆ°å†…å­˜ç¼“å­˜
      const memoryItem = this.memoryCache.get(key);
      if (memoryItem) {
        // æ£€æŸ¥æ˜¯å¦è¿‡æœŸ
        if (Date.now() < memoryItem.expiry) {
          return memoryItem.value;
        }
        this.memoryCache.delete(key);
      }

      return null;
    } catch (error) {
      logger.error('ç¼“å­˜è·å–å¤±è´¥', { key, error: error.message });
      return null;
    }
  }

  /**
   * è®¾ç½®ç¼“å­˜å€¼
   * @param {string} key - ç¼“å­˜é”®
   * @param {any} value - ç¼“å­˜å€¼
   * @param {number} ttl - è¿‡æœŸæ—¶é—´ï¼ˆç§’ï¼‰
   */
  async set(key, value, ttl = this.defaultTTL) {
    try {
      const serializedValue = JSON.stringify(value);

      // ä½¿ç”¨Redis
      if (this.redisClient) {
        await this.redisClient.setex(key, ttl, serializedValue);
      }

      // åŒæ—¶å­˜å‚¨åˆ°å†…å­˜ç¼“å­˜
      this.setMemoryCache(key, value, ttl);

      logger.debug('ç¼“å­˜è®¾ç½®æˆåŠŸ', { key, ttl });
    } catch (error) {
      logger.error('ç¼“å­˜è®¾ç½®å¤±è´¥', { key, error: error.message });
    }
  }

  /**
   * è®¾ç½®å†…å­˜ç¼“å­˜
   * @param {string} key - ç¼“å­˜é”®
   * @param {any} value - ç¼“å­˜å€¼
   * @param {number} ttl - è¿‡æœŸæ—¶é—´ï¼ˆç§’ï¼‰
   */
  setMemoryCache(key, value, ttl) {
    // å¦‚æœå†…å­˜ç¼“å­˜å·²æ»¡ï¼Œåˆ é™¤æœ€æ—§çš„æ¡ç›®
    if (this.memoryCache.size >= this.maxMemoryItems) {
      const firstKey = this.memoryCache.keys().next().value;
      this.memoryCache.delete(firstKey);
    }

    const expiry = Date.now() + ttl * 1000;
    this.memoryCache.set(key, { value, expiry });
  }

  /**
   * åˆ é™¤ç¼“å­˜
   * @param {string} key - ç¼“å­˜é”®
   */
  async delete(key) {
    try {
      if (this.redisClient) {
        await this.redisClient.del(key);
      }
      this.memoryCache.delete(key);
      logger.debug('ç¼“å­˜åˆ é™¤æˆåŠŸ', { key });
    } catch (error) {
      logger.error('ç¼“å­˜åˆ é™¤å¤±è´¥', { key, error: error.message });
    }
  }

  /**
   * æ‰¹é‡åˆ é™¤ç¼“å­˜ï¼ˆæ”¯æŒæ¨¡å¼åŒ¹é…ï¼‰
   * @param {string} pattern - åŒ¹é…æ¨¡å¼
   */
  async invalidate(pattern) {
    try {
      if (this.redisClient) {
        const keys = await this.redisClient.keys(pattern);
        if (keys.length > 0) {
          await this.redisClient.del(...keys);
        }
      }

      // æ¸…ç†å†…å­˜ç¼“å­˜
      const regex = new RegExp(pattern.replace(/\*/g, '.*'));
      for (const key of this.memoryCache.keys()) {
        if (regex.test(key)) {
          this.memoryCache.delete(key);
        }
      }

      logger.debug('ç¼“å­˜æ‰¹é‡åˆ é™¤æˆåŠŸ', { pattern });
    } catch (error) {
      logger.error('ç¼“å­˜æ‰¹é‡åˆ é™¤å¤±è´¥', { pattern, error: error.message });
    }
  }

  /**
   * æ¸…ç©ºæ‰€æœ‰ç¼“å­˜
   */
  async clear() {
    try {
      if (this.redisClient) {
        await this.redisClient.flushdb();
      }
      this.memoryCache.clear();
      logger.info('æ‰€æœ‰ç¼“å­˜å·²æ¸…ç©º');
    } catch (error) {
      logger.error('æ¸…ç©ºç¼“å­˜å¤±è´¥', { error: error.message });
    }
  }

  /**
   * è·å–ç¼“å­˜ç»Ÿè®¡ä¿¡æ¯
   */
  async getStats() {
    const stats = {
      memoryCache: {
        size: this.memoryCache.size,
        maxSize: this.maxMemoryItems
      },
      redis: {
        connected: !!this.redisClient
      }
    };

    if (this.redisClient) {
      try {
        const info = await this.redisClient.info('memory');
        stats.redis.memory = info;
      } catch (error) {
        logger.warn('è·å–Redisç»Ÿè®¡ä¿¡æ¯å¤±è´¥', { error: error.message });
      }
    }

    return stats;
  }

  /**
   * ç¼“å­˜è£…é¥°å™¨
   * @param {string} keyPrefix - ç¼“å­˜é”®å‰ç¼€
   * @param {number} ttl - è¿‡æœŸæ—¶é—´
   * @returns {Function} è£…é¥°å™¨å‡½æ•°
   */
  cached(keyPrefix, ttl = this.defaultTTL) {
    return (target, propertyName, descriptor) => {
      const originalMethod = descriptor.value;

      descriptor.value = async function (...args) {
        const cacheKey = `${keyPrefix}:${JSON.stringify(args)}`;

        // å°è¯•ä»ç¼“å­˜è·å–
        const cachedResult = await this.get(cacheKey);
        if (cachedResult !== null) {
          return cachedResult;
        }

        // æ‰§è¡ŒåŸæ–¹æ³•
        const result = await originalMethod.apply(this, args);

        // å­˜å‚¨åˆ°ç¼“å­˜
        await this.set(cacheKey, result, ttl);

        return result;
      }.bind(this);

      return descriptor;
    };
  }

  /**
   * æ¸…ç†è¿‡æœŸçš„å†…å­˜ç¼“å­˜
   */
  cleanupExpiredMemoryCache() {
    const now = Date.now();
    for (const [key, item] of this.memoryCache.entries()) {
      if (now >= item.expiry) {
        this.memoryCache.delete(key);
      }
    }
  }
}

/**
 * ç¼“å­˜ä¸­é—´ä»¶å·¥å‚
 * @param {number} ttl - ç¼“å­˜æ—¶é—´ï¼ˆç§’ï¼‰
 * @param {Function} keyGenerator - ç¼“å­˜é”®ç”Ÿæˆå‡½æ•°
 * @returns {Function} Expressä¸­é—´ä»¶
 */
const createCacheMiddleware =
  (ttl = 300, keyGenerator = null) =>
    async (req, res, next) => {
      try {
      // ç”Ÿæˆç¼“å­˜é”®
        const cacheKey = keyGenerator
          ? keyGenerator(req)
          : `api:${req.method}:${req.originalUrl}:${JSON.stringify(req.query)}`;

        // å°è¯•ä»ç¼“å­˜è·å–
        const cachedData = await cacheManager.get(cacheKey);
        if (cachedData) {
          return res.json({
            success: true,
            data: cachedData,
            cached: true,
            timestamp: new Date().toISOString()
          });
        }

        // é‡å†™res.jsonæ–¹æ³•ä»¥ç¼“å­˜å“åº”
        const originalJson = res.json;
        res.json = function (data) {
        // åªç¼“å­˜æˆåŠŸçš„å“åº”
          if (data.success !== false) {
            cacheManager.set(cacheKey, data, ttl).catch((error) => {
              logger.error('ç¼“å­˜å“åº”å¤±è´¥', { cacheKey, error: error.message });
            });
          }
          return originalJson.call(this, data);
        };

        next();
      } catch (error) {
        logger.error('ç¼“å­˜ä¸­é—´ä»¶é”™è¯¯', { error: error.message });
        next();
      }
    };

