export function validateConfig() {
  const errors = [];

  // éªŒè¯JWTå¯†é’¥é•¿åº¦
  if (config.jwt.secret && config.jwt.secret.length < 32) {
    errors.push('JWT_SECRET é•¿åº¦å¿…é¡»è‡³å°‘32ä¸ªå­—ç¬¦');
  }

  // éªŒè¯ç«¯å£èŒƒå›´
  if (config.app.port < 1 || config.app.port > 65535) {
    errors.push('PORT å¿…é¡»åœ¨1-65535èŒƒå›´å†…');
  }

  // éªŒè¯æ—¥å¿—ç›®å½•
  if (config.logging.enableFile && !config.logging.logDir) {
    errors.push('å¯ç”¨æ–‡ä»¶æ—¥å¿—æ—¶å¿…é¡»è®¾ç½® LOG_DIR');
  }

  // éªŒè¯ä¸Šä¼ ç›®å½•
  if (!config.upload.uploadDir) {
    errors.push('å¿…é¡»è®¾ç½® UPLOAD_DIR');
  }

  if (errors.length > 0) {
    throw new Error(`é…ç½®éªŒè¯å¤±è´¥:\n${errors.join('\n')}`);
  }

  console.log(`[CONFIG] é…ç½®éªŒè¯é€šè¿‡ï¼Œç¯å¢ƒ: ${config.app.env}`);
}

