      stack: error.stack
    },
    request: {
      method: req.method,
      url: req.originalUrl,
      ip: req.ip,
      userAgent: req.get('User-Agent'),
      userId: req.user?.id,
      body: req.method !== 'GET' ? req.body : undefined
    },
    timestamp: new Date().toISOString()
  };

  // æ ¹æ®é”™è¯¯çº§åˆ«è®°å½•ä¸åŒçº§åˆ«çš„æ—¥å¿—
  if (error.statusCode >= 500) {
    logger.error('æœåŠ¡å™¨é”™è¯¯', logData);
  } else if (error.statusCode >= 400) {
    logger.warn('å®¢æˆ·ç«¯é”™è¯¯', logData);
  } else {
    logger.info('è¯·æ±‚é”™è¯¯', logData);
  }

  // æ„å»ºå“åº”æ•°æ®
  const responseData = {
    success: false,
    error: {
      code: error.errorCode,
      message: error.message,
      details: error.details,
      timestamp: error.timestamp
    }
  };

  // å¼€å‘ç¯å¢ƒä¸‹åŒ…å«å †æ ˆä¿¡æ¯
  if (process.env.NODE_ENV === 'development') {
    responseData.error.stack = error.stack;
    responseData.error.request = {
      method: req.method,
      url: req.originalUrl
    };
  }

  // å‘é€é”™è¯¯å“åº”
  res.status(error.statusCode).json(responseData);
};

