  // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 44 è¡Œ)

  // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 44 è¡Œ)

  constructor(options = {}) {
    this.options = {
      enableMetrics: options.enableMetrics !== false,
      enableLogging: options.enableLogging !== false,
      slowRequestThreshold: options.slowRequestThreshold || 1000, // 1ç§’
      memoryWarningThreshold: options.memoryWarningThreshold || 0.8, // 80%
      cpuWarningThreshold: options.cpuWarningThreshold || 0.8, // 80%
      ...options
    };

    this.metrics = {
      requests: {
        total: 0,
        success: 0,
        error: 0,
        slow: 0
      },
      responseTime: {
        min: Infinity,
        max: 0,
        avg: 0,
        p95: 0,
        p99: 0
      },
      memory: {
        used: 0,
        free: 0,
        total: 0,
        percentage: 0
      },
      cpu: {
        usage: 0,
        loadAverage: [0, 0, 0]
      },
      errors: new Map(),
      endpoints: new Map()
    };

    this.responseTimes = [];
    this.maxResponseTimeHistory = 1000; // ä¿ç•™æœ€è¿‘1000ä¸ªå“åº”æ—¶é—´

    // æš‚æ—¶ç¦ç”¨ç³»ç»Ÿç›‘æ§ï¼Œé¿å…é«˜èµ„æºä½¿ç”¨ç‡è­¦å‘Š
    // this.startSystemMonitoring();
  }

  /**
   * å¯åŠ¨ç³»ç»Ÿç›‘æ§
   */
  startSystemMonitoring() {
    setInterval(() => {
      this.updateSystemMetrics();
    }, 5000); // æ¯5ç§’æ›´æ–°ä¸€æ¬¡
  }

  // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 29 è¡Œ)

  // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 29 è¡Œ)

  // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 29 è¡Œ)

  // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 29 è¡Œ)

  /**
   * æ›´æ–°ç³»ç»ŸæŒ‡æ ‡
   */
  updateSystemMetrics() {
    // å†…å­˜ä½¿ç”¨æƒ…å†µ
    const memUsage = process.memoryUsage();
    const totalMem = os.totalmem();
    const freeMem = os.freemem();
    const usedMem = totalMem - freeMem;

    this.metrics.memory = {
      used: usedMem,
      free: freeMem,
      total: totalMem,
      percentage: usedMem / totalMem,
      heap: {
        used: memUsage.heapUsed,
        total: memUsage.heapTotal,
        external: memUsage.external,
        rss: memUsage.rss
      }
    };

    // CPUè´Ÿè½½
    this.metrics.cpu = {
      loadAverage: os.loadavg(),
      cpuCount: os.cpus().length
    };

    // æ£€æŸ¥è­¦å‘Šé˜ˆå€¼
    this.checkThresholds();
  }

  /**
   * æ£€æŸ¥æ€§èƒ½é˜ˆå€¼
   */
  checkThresholds() {
    // å†…å­˜è­¦å‘Š
    if (this.metrics.memory.percentage > this.options.memoryWarningThreshold) {
      logger.warn('å†…å­˜ä½¿ç”¨ç‡è¿‡é«˜', {
        usage: `${(this.metrics.memory.percentage * 100).toFixed(2)}%`,
        used: `${(this.metrics.memory.used / 1024 / 1024 / 1024).toFixed(2)}GB`,
        total: `${(this.metrics.memory.total / 1024 / 1024 / 1024).toFixed(2)}GB`
      });
    }

    // CPUè´Ÿè½½è­¦å‘Š
    const avgLoad = this.metrics.cpu.loadAverage[0];
    const cpuUsage = avgLoad / this.metrics.cpu.cpuCount;
    if (cpuUsage > this.options.cpuWarningThreshold) {
      logger.warn('CPUè´Ÿè½½è¿‡é«˜', {
        usage: `${(cpuUsage * 100).toFixed(2)}%`,
        loadAverage: this.metrics.cpu.loadAverage,
        cpuCount: this.metrics.cpu.cpuCount
      });
    }
  }

  /**
   * è®°å½•è¯·æ±‚æŒ‡æ ‡
   */
  recordRequest(req, res, responseTime) {
    this.metrics.requests.total++;

    // è®°å½•å“åº”æ—¶é—´
    this.responseTimes.push(responseTime);
    if (this.responseTimes.length > this.maxResponseTimeHistory) {
      this.responseTimes.shift();
    }

    // æ›´æ–°å“åº”æ—¶é—´ç»Ÿè®¡
    this.updateResponseTimeStats();

    // è®°å½•æ…¢è¯·æ±‚
    if (responseTime > this.options.slowRequestThreshold) {
      this.metrics.requests.slow++;
      logger.warn('æ…¢è¯·æ±‚æ£€æµ‹', {
        method: req.method,
        path: req.path,
        responseTime: `${responseTime}ms`,
        userAgent: req.get('User-Agent'),
        ip: req.ip
      });
    }

    // è®°å½•çŠ¶æ€ç 
    if (res.statusCode >= 200 && res.statusCode < 400) {
      this.metrics.requests.success++;
    } else if (res.statusCode >= 400) {
      this.metrics.requests.error++;
    }

    // è®°å½•ç«¯ç‚¹ç»Ÿè®¡
    const endpoint = `${req.method} ${req.route?.path || req.path}`;
    if (!this.metrics.endpoints.has(endpoint)) {
      this.metrics.endpoints.set(endpoint, {
        count: 0,
        totalTime: 0,
        avgTime: 0,
        minTime: Infinity,
        maxTime: 0,
        errors: 0
      });
    }

    const endpointStats = this.metrics.endpoints.get(endpoint);
    endpointStats.count++;
    endpointStats.totalTime += responseTime;
    endpointStats.avgTime = endpointStats.totalTime / endpointStats.count;
    endpointStats.minTime = Math.min(endpointStats.minTime, responseTime);
    endpointStats.maxTime = Math.max(endpointStats.maxTime, responseTime);

    if (res.statusCode >= 400) {
      endpointStats.errors++;
    }
  }

  /**
   * æ›´æ–°å“åº”æ—¶é—´ç»Ÿè®¡
   */
  updateResponseTimeStats() {
    if (this.responseTimes.length === 0) {
      return;
    }

    const sorted = [...this.responseTimes].sort((a, b) => a - b);
    const len = sorted.length;

    this.metrics.responseTime = {
      min: sorted[0],
      max: sorted[len - 1],
      avg: sorted.reduce((sum, time) => sum + time, 0) / len,
      p95: sorted[Math.floor(len * 0.95)],
      p99: sorted[Math.floor(len * 0.99)]
    };
  }

  /**
   * è®°å½•é”™è¯¯
   */
  recordError(error, req) {
    const errorKey = `${error.name}: ${error.message}`;
    const errorCount = this.metrics.errors.get(errorKey) || 0;
    this.metrics.errors.set(errorKey, errorCount + 1);

    logger.error('è¯·æ±‚å¤„ç†é”™è¯¯', {
      error: error.message,
      stack: error.stack,
      method: req.method,
      path: req.path,
      ip: req.ip,
      userAgent: req.get('User-Agent')
    });
  }

  /**
   * è·å–æ€§èƒ½æŒ‡æ ‡
   */
  getMetrics() {
    return {
      ...this.metrics,
      uptime: process.uptime(),
      timestamp: new Date().toISOString(),
      nodeVersion: process.version,
      platform: os.platform(),
      arch: os.arch()
    };
  }

  /**
   * é‡ç½®æŒ‡æ ‡
   */
  resetMetrics() {
    this.metrics.requests = {
      total: 0,
      success: 0,
      error: 0,
      slow: 0
    };
    this.metrics.errors.clear();
    this.metrics.endpoints.clear();
    this.responseTimes = [];
  }

  /**
   * æ€§èƒ½ç›‘æ§ä¸­é—´ä»¶
   */
  middleware() {
    return (req, res, next) => {
      const startTime = performance.now();

      // è®°å½•è¯·æ±‚å¼€å§‹æ—¶é—´
      req.startTime = startTime;

      // ç›‘å¬å“åº”ç»“æŸäº‹ä»¶
      res.on('finish', () => {
        const endTime = performance.now();
        const responseTime = Math.round(endTime - startTime);

        if (this.options.enableMetrics) {
          this.recordRequest(req, res, responseTime);
        }

        if (this.options.enableLogging) {
          logger.info('è¯·æ±‚å®Œæˆ', {
            method: req.method,
            path: req.path,
            statusCode: res.statusCode,
            responseTime: `${responseTime}ms`,
            ip: req.ip,
            userAgent: req.get('User-Agent')
          });
        }
      });

      // ç›‘å¬é”™è¯¯äº‹ä»¶
      res.on('error', (error) => {
        if (this.options.enableMetrics) {
          this.recordError(error, req);
        }
      });

      next();
    };

    // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 33 è¡Œ)

    // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 36 è¡Œ)

    // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 39 è¡Œ)

    // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 42 è¡Œ)
  }
}

/**
 * å¥åº·æ£€æŸ¥ä¸­é—´ä»¶
 */
export const healthCheck = (monitor) => (req, res) => {
  const metrics = monitor.getMetrics();

  // åˆ¤æ–­ç³»ç»Ÿå¥åº·çŠ¶æ€
  const isHealthy =
    metrics.memory.percentage < 0.9 && // å†…å­˜ä½¿ç”¨ç‡å°äº90%
    metrics.cpu.loadAverage[0] / metrics.cpu.cpuCount < 0.9 && // CPUè´Ÿè½½å°äº90%
    metrics.responseTime.avg < 2000; // å¹³å‡å“åº”æ—¶é—´å°äº2ç§’

  const status = isHealthy ? 'healthy' : 'unhealthy';
  const statusCode = isHealthy ? 200 : 503;

  res.status(statusCode).json({
    status,
    timestamp: new Date().toISOString(),
    uptime: process.uptime(),
    memory: {
      usage: `${(metrics.memory.percentage * 100).toFixed(2)}%`,
      used: `${(metrics.memory.used / 1024 / 1024 / 1024).toFixed(2)}GB`,
      total: `${(metrics.memory.total / 1024 / 1024 / 1024).toFixed(2)}GB`
    },
    cpu: {
      loadAverage: metrics.cpu.loadAverage,
      usage: `${((metrics.cpu.loadAverage[0] / metrics.cpu.cpuCount) * 100).toFixed(2)}%`
    },
    requests: metrics.requests,
    responseTime: {
      avg: `${metrics.responseTime.avg.toFixed(2)}ms`,

      // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 21 è¡Œ)

      // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 21 è¡Œ)

      // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 21 è¡Œ)

      // TODO: è€ƒè™‘å°†æ­¤å‡½æ•°æ‹†åˆ†ä¸ºæ›´å°çš„å‡½æ•° (å½“å‰ 21 è¡Œ)

      p95: `${metrics.responseTime.p95}ms`,
      p99: `${metrics.responseTime.p99}ms`
    }
  });
};

/**
 * æ€§èƒ½æŒ‡æ ‡APIä¸­é—´ä»¶
 */
export const metricsEndpoint = (monitor) => (req, res) => {
  const metrics = monitor.getMetrics();

  // è½¬æ¢ç«¯ç‚¹ç»Ÿè®¡ä¸ºæ•°ç»„æ ¼å¼
  const endpoints = Array.from(metrics.endpoints.entries()).map(([path, stats]) => ({
    path,
    ...stats
  }));

  // è½¬æ¢é”™è¯¯ç»Ÿè®¡ä¸ºæ•°ç»„æ ¼å¼
  const errors = Array.from(metrics.errors.entries()).map(([error, count]) => ({
    error,
    count
  }));

  res.json({
    ...metrics,
    endpoints,
    errors
  });
};

