import { MATH_CONSTANTS, TIME_INTERVALS } from '../constants/MathConstants.js';
import { defaultLogger } from './logger.js';

const logger = defaultLogger.child('Cache');

class MemoryCache {
  constructor() {
    this.cache = new Map();
    this.ttlMap = new Map();
    this.defaultTTL = TIME_INTERVALS.FIVE_MINUTES_MS; // é»˜è®¤5åˆ†é’Ÿè¿‡æœŸ

    // å®šæœŸæ¸…ç†è¿‡æœŸç¼“å­˜
    setInterval(() => {
      this.cleanup();
    }, MATH_CONSTANTS.SECONDS_PER_MINUTE * MATH_CONSTANTS.MILLISECONDS_PER_SECOND); // æ¯åˆ†é’Ÿæ¸…ç†ä¸€æ¬¡
  }

  /**
   * è®¾ç½®ç¼“å­˜
   * @param {string} key - ç¼“å­˜é”®
   * @param {any} value - ç¼“å­˜å€¼
   * @param {number} ttl - è¿‡æœŸæ—¶é—´ï¼ˆæ¯«ç§’ï¼‰ï¼Œé»˜è®¤ä½¿ç”¨defaultTTL
   */
  set(key, value, ttl = this.defaultTTL) {
    const expiresAt = Date.now() + ttl;
    this.cache.set(key, value);
    this.ttlMap.set(key, expiresAt);
  }

  /**
   * è·å–ç¼“å­˜
   * @param {string} key - ç¼“å­˜é”®
   * @returns {any|null} ç¼“å­˜å€¼æˆ–null
   */
  get(key) {
    const expiresAt = this.ttlMap.get(key);

    // æ£€æŸ¥æ˜¯å¦è¿‡æœŸ
    if (!expiresAt || Date.now() > expiresAt) {
      this.delete(key);
      return null;
    }

    return this.cache.get(key);
  }

  /**
   * åˆ é™¤ç¼“å­˜
   * @param {string} key - ç¼“å­˜é”®
   */
  delete(key) {
    this.cache.delete(key);
    this.ttlMap.delete(key);
  }

  /**
   * æ¸…ç©ºæ‰€æœ‰ç¼“å­˜
   */
  clear() {
    this.cache.clear();
    this.ttlMap.clear();
  }

  /**
   * æ£€æŸ¥ç¼“å­˜æ˜¯å¦å­˜åœ¨ä¸”æœªè¿‡æœŸ
   * @param {string} key - ç¼“å­˜é”®
   * @returns {boolean}
   */
  has(key) {
    const expiresAt = this.ttlMap.get(key);
    if (!expiresAt || Date.now() > expiresAt) {
      this.delete(key);
      return false;
    }
    return this.cache.has(key);
  }

  /**
   * è·å–ç¼“å­˜ç»Ÿè®¡ä¿¡æ¯
   * @returns {object} ç»Ÿè®¡ä¿¡æ¯
   */
  getStats() {
    return {
      size: this.cache.size,
      keys: Array.from(this.cache.keys())
    };
  }

  /**
   * æ¸…ç†è¿‡æœŸç¼“å­˜
   */
  cleanup() {
    const now = Date.now();
    const expiredKeys = [];

    for (const [key, expiresAt] of this.ttlMap.entries()) {
      if (now > expiresAt) {
        expiredKeys.push(key);
      }
    }

    expiredKeys.forEach((key) => this.delete(key));

    if (expiredKeys.length > 0) {
      logger.info(`æ¸…ç†äº† ${expiredKeys.length} ä¸ªè¿‡æœŸç¼“å­˜é¡¹`);
    }
  }

  /**
   * ç”Ÿæˆç¼“å­˜é”®
   * @param {string} prefix - å‰ç¼€
   * @param {object} params - å‚æ•°å¯¹è±¡
   * @returns {string} ç¼“å­˜é”®
   */
  generateKey(prefix, params = {}) {
    const sortedParams = Object.keys(params)
      .sort()
      .map((key) => `${key}:${params[key]}`)
      .join('|');

    return `${prefix}:${sortedParams}`;
  }
}

// åˆ›å»ºå…¨å±€ç¼“å­˜å®ä¾‹
const cache = new MemoryCache();

/**
 * ç¼“å­˜ä¸­é—´ä»¶å·¥å‚å‡½æ•°
 * @param {number} ttl - ç¼“å­˜è¿‡æœŸæ—¶é—´ï¼ˆæ¯«ç§’ï¼‰
 * @param {function} keyGenerator - ç¼“å­˜é”®ç”Ÿæˆå‡½æ•°
 * @returns {function} Expressä¸­é—´ä»¶
 */
export function createCacheMiddleware(ttl = TIME_INTERVALS.FIVE_MINUTES_MS, keyGenerator = null) {
  return (req, res, next) => {
    // åªç¼“å­˜GETè¯·æ±‚
    if (req.method !== 'GET') {
      return next();
    }

    // ç”Ÿæˆç¼“å­˜é”®
    const cacheKey = keyGenerator ? keyGenerator(req) : cache.generateKey(req.path, req.query);

    // å°è¯•ä»ç¼“å­˜è·å–æ•°æ®
    const cachedData = cache.get(cacheKey);
    if (cachedData) {
      logger.debug(`ç¼“å­˜å‘½ä¸­: ${cacheKey}`);
      // è®¾ç½®ç¼“å­˜å‘½ä¸­å¤´ä¿¡æ¯
      res.setHeader('X-Cache-Hit', 'true');
      res.setHeader('X-Cache-Key', cacheKey);
      return res.json(cachedData);
    }

    // è®¾ç½®ç¼“å­˜æœªå‘½ä¸­å¤´ä¿¡æ¯
    res.setHeader('X-Cache-Hit', 'false');
    res.setHeader('X-Cache-Key', cacheKey);

    // é‡å†™res.jsonæ–¹æ³•ä»¥ç¼“å­˜å“åº”
    const originalJson = res.json;
    res.json = function (data) {
      // åªç¼“å­˜æˆåŠŸçš„å“åº”
      if (res.statusCode === 200) {
        cache.set(cacheKey, data, ttl);
        logger.debug(`ç¼“å­˜å­˜å‚¨: ${cacheKey}`);
      }
      return originalJson.call(this, data);
    };

    next();
  };
}

/**
 * æ¸…é™¤ç‰¹å®šå‰ç¼€çš„ç¼“å­˜
 * @param {string} prefix - ç¼“å­˜é”®å‰ç¼€
 */
export function clearCacheByPrefix(prefix) {
  const stats = cache.getStats();
  const keysToDelete = stats.keys.filter((key) => key.startsWith(prefix));

  keysToDelete.forEach((key) => cache.delete(key));

  logger.info(`æ¸…é™¤äº† ${keysToDelete.length} ä¸ªå‰ç¼€ä¸º "${prefix}" çš„ç¼“å­˜é¡¹`);
}

/**
 * é¢„çƒ­ç¼“å­˜å‡½æ•°
 * @param {string} key - ç¼“å­˜é”®
 * @param {function} dataLoader - æ•°æ®åŠ è½½å‡½æ•°
 * @param {number} ttl - è¿‡æœŸæ—¶é—´
 */
export async function warmupCache(key, dataLoader, ttl = TIME_INTERVALS.FIVE_MINUTES_MS) {
  try {
    const data = await dataLoader();
    cache.set(key, data, ttl);
    logger.info(`é¢„çƒ­ç¼“å­˜: ${key}`);
  } catch (error) {
    logger.error(`é¢„çƒ­ç¼“å­˜å¤±è´¥: ${key}`, { error: error.message, stack: error.stack });
  }
}

export { cache };
export default cache;
