    /**
     * æ•°æ®æ ‡å‡†åŒ–å¤„ç†
     * @param {string} standardId æ•°æ®æ ‡å‡†ID
     * @param {Object} data åŸå§‹æ•°æ®
     * @returns {Object} æ ‡å‡†åŒ–åçš„æ•°æ®
     */
    async standardizeData(standardId, data) {
        const standard = this.dataStandards.get(standardId);
        if (!standard) {
            throw new Error(`æ•°æ®æ ‡å‡†ä¸å­˜åœ¨: ${standardId}`);
        }

        const standardizedData = {};
        const validationErrors = [];

        // å­—æ®µæ ‡å‡†åŒ–å’ŒéªŒè¯
        for (const [fieldName, fieldSpec] of Object.entries(standard.fields)) {
            const value = data[fieldName];

            // å¿…å¡«å­—æ®µæ£€æŸ¥
            if (fieldSpec.required && (value === undefined || value === null || value === '')) {
                validationErrors.push(`å¿…å¡«å­—æ®µç¼ºå¤±: ${fieldName}`);
                continue;
            }

            if (value !== undefined && value !== null) {
                try {
                    // ç±»å‹è½¬æ¢å’ŒéªŒè¯
                    const standardizedValue = this.standardizeFieldValue(value, fieldSpec);
                    standardizedData[fieldName] = standardizedValue;
                } catch (error) {
                    validationErrors.push(`å­—æ®µ ${fieldName} æ ‡å‡†åŒ–å¤±è´¥: ${error.message}`);
                }
            }
        }

        // ä¸šåŠ¡è§„åˆ™éªŒè¯
        for (const ruleName of standard.validation_rules) {
            try {
                const ruleResult = await this.validateBusinessRule(ruleName, standardizedData, standard);
                if (!ruleResult.valid) {
                    validationErrors.push(`ä¸šåŠ¡è§„åˆ™éªŒè¯å¤±è´¥ (${ruleName}): ${ruleResult.message}`);
                }
            } catch (error) {
                validationErrors.push(`ä¸šåŠ¡è§„åˆ™æ£€æŸ¥å¤±è´¥ (${ruleName}): ${error.message}`);
            }
        }

        return {
            standardized_data: standardizedData,
            validation_errors: validationErrors,
            is_valid: validationErrors.length === 0,
            standard_id: standardId,
            processed_at: new Date().toISOString()
        };
    }

