    standardizeFieldValue(value, fieldSpec) {
        // ç±»å‹è½¬æ¢
        switch (fieldSpec.type) {
            case 'number':
                const numValue = Number(value);
                if (isNaN(numValue)) {
                    throw new Error(`æ— æ³•è½¬æ¢ä¸ºæ•°å­—: ${value}`);
                }
                if (fieldSpec.min !== undefined && numValue < fieldSpec.min) {
                    throw new Error(`å€¼å°äºæœ€å°å€¼ ${fieldSpec.min}: ${numValue}`);
                }
                if (fieldSpec.max !== undefined && numValue > fieldSpec.max) {
                    throw new Error(`å€¼å¤§äºæœ€å¤§å€¼ ${fieldSpec.max}: ${numValue}`);
                }
                return fieldSpec.precision ? Number(numValue.toFixed(fieldSpec.precision)) : numValue;

            case 'string':
                const strValue = String(value);
                if (fieldSpec.pattern && !new RegExp(fieldSpec.pattern).test(strValue)) {
                    throw new Error(`æ ¼å¼ä¸åŒ¹é…: ${strValue}`);
                }
                if (fieldSpec.enum && !fieldSpec.enum.includes(strValue)) {
                    throw new Error(`å€¼ä¸åœ¨å…è®¸èŒƒå›´å†…: ${strValue}`);
                }
                return strValue;

            case 'datetime':
                const dateValue = new Date(value);
                if (isNaN(dateValue.getTime())) {
                    throw new Error(`æ— æ•ˆçš„æ—¥æœŸæ—¶é—´: ${value}`);
                }
                return dateValue.toISOString();

            case 'date':
                const date = new Date(value);
                if (isNaN(date.getTime())) {
                    throw new Error(`æ— æ•ˆçš„æ—¥æœŸ: ${value}`);
                }
                return date.toISOString().split('T')[MATH_CONSTANTS.ZERO];

            default:
                return value;
        }
    }

