  // 执行优化策略
  async executeOptimization() {
    const results = [];

    for (const rule of this.optimizationRules) {
      if (!rule.enabled) {
        continue;
      }

      try {
        const result = await this.applyOptimizationRule(rule);
        results.push(result);
      } catch (error) {
        console.error(`执行优化规则 ${rule.name} 失败:`, error);
      }
    }

    this.emit('optimizationExecuted', results);
    return results;
