  ACCESS_DASHBOARD: 'access_dashboard'
};

// 角色权限映射
const rolePermissions = {
  [roles.ADMIN]: Object.values(permissions),
  [roles.ENERGY_MANAGER]: [
    permissions.VIEW_ENERGY_DATA,
