/**
 * 异步错误包装器
 * 自动捕获异步函数中的错误并传递给错误处理中间件
 */
export const asyncHandler = (fn) => (req, res, next) => {
  Promise.resolve(fn(req, res, next)).catch(next);
};

/**
 * 404错误处理中间件
 */
export const notFoundHandler = (req, res, next) => {
  const error = new NotFoundError(`路由 ${req.originalUrl}`);
  next(error);
};

/**
 * 进程异常处理
 */
export const setupProcessHandlers = () => {
  // 处理未捕获的异常
  process.on('uncaughtException', (err) => {
    console.error('未捕获的异常:', err);
    console.error('应用程序将退出...');
    process.exit(1);
  });

  // 处理未处理的Promise拒绝
  process.on('unhandledRejection', (reason, promise) => {
    console.error('未处理的Promise拒绝:', reason);
    console.error('Promise:', promise);
    // 在生产环境中可能需要退出进程
    if (process.env.NODE_ENV === 'production') {
      console.error('应用程序将退出...');
      process.exit(1);
    }
  });

  // 优雅关闭处理
  const gracefulShutdown = (signal) => {
    console.log(`收到 ${signal} 信号，开始优雅关闭...`);
    // 这里可以添加清理逻辑，如关闭数据库连接、停止定时器等
    process.exit(0);
  };

  process.on('SIGTERM', () => gracefulShutdown('SIGTERM'));
  process.on('SIGINT', () => gracefulShutdown('SIGINT'));
};

export default {
  AppError,
  ValidationError,
  AuthenticationError,
  AuthorizationError,
  NotFoundError,
  DatabaseError,
  ExternalServiceError,
  errorHandler,
  asyncHandler,
  notFoundHandler,
  setupProcessHandlers
};

import path from 'path';
import fs from 'fs';

/**
 * 性能监控中间件
 * 用于跟踪API响应时间、缓存命中率等性能指标
 */
class PerformanceMonitor {
  constructor() {
    this.metrics = {
      requests: 0,
      totalResponseTime: 0,
      cacheHits: 0,
      cacheMisses: 0,
      endpoints: new Map()
    };

    this.logFile = path.join(process.cwd(), 'logs', 'performance.log');
    this.reportFile = path.join(process.cwd(), 'test-results', 'performance_monitor.csv');

    // 确保日志目录存在
    this.ensureLogDirectory();

    // 定期生成性能报告
    this.startPeriodicReporting();
  }

  ensureLogDirectory() {
    const logDir = path.dirname(this.logFile);
    const reportDir = path.dirname(this.reportFile);

    if (!fs.existsSync(logDir)) {
      fs.mkdirSync(logDir, { recursive: true });
    }

    if (!fs.existsSync(reportDir)) {
      fs.mkdirSync(reportDir, { recursive: true });
    }
  }

  /**
   * 创建性能监控中间件
   */
  middleware() {
    return (req, res, next) => {
      try {
        const startTime = Date.now();
        // 修复：安全地获取路径，避免未定义错误
        let endpoint = req.path;
        try {
          endpoint = `${req.method} ${req.route?.path || req.path}`;
        } catch (e) {
          // 如果获取route.path失败，使用req.path
          endpoint = `${req.method} ${req.path}`;
        }

        // 记录请求开始
        this.recordRequestStart(endpoint);

        // 监听响应结束
        res.on('finish', () => {
          try {
            const responseTime = Date.now() - startTime;
            const cacheHit = res.getHeader('X-Cache-Hit') === 'true';

            // 使用process.nextTick延迟记录，避免阻塞响应
            process.nextTick(() => {
              this.recordRequestEnd(endpoint, responseTime, cacheHit, res.statusCode);
            });
          } catch (error) {
            console.error('性能监控记录响应结束失败:', error);
          }
        });
      } catch (error) {
        console.error('性能监控中间件错误:', error);
      }

      // 无论如何都继续处理请求
      next();
    };
  }

  /**
   * 记录请求开始
   */
  recordRequestStart(endpoint) {
    this.metrics.requests++;

    if (!this.metrics.endpoints.has(endpoint)) {
      this.metrics.endpoints.set(endpoint, {
        requests: 0,
        totalTime: 0,
        cacheHits: 0,
        cacheMisses: 0,
        errors: 0
      });
    }

    this.metrics.endpoints.get(endpoint).requests++;
  }

  /**
   * 记录请求结束
   */
  recordRequestEnd(endpoint, responseTime, cacheHit, statusCode) {
    this.metrics.totalResponseTime += responseTime;

    const endpointMetrics = this.metrics.endpoints.get(endpoint);
    if (endpointMetrics) {
      endpointMetrics.totalTime += responseTime;

      if (cacheHit) {
        this.metrics.cacheHits++;
        endpointMetrics.cacheHits++;
      } else {
        this.metrics.cacheMisses++;
        endpointMetrics.cacheMisses++;
      }

      if (statusCode >= 400) {
        endpointMetrics.errors++;
      }
    }

    // 记录到日志文件
    this.logPerformanceData(endpoint, responseTime, cacheHit, statusCode);
  }

  /**
   * 记录性能数据到日志文件
   */
  logPerformanceData(endpoint, responseTime, cacheHit, statusCode) {
    try {
      const logEntry = {
        timestamp: new Date().toISOString(),
        endpoint,
        responseTime,
        cacheHit,
        statusCode
      };

      const logLine = `${JSON.stringify(logEntry)}\n`;

      // 使用异步文件操作，避免阻塞请求
      // 使用setImmediate进一步延迟日志写入，确保不影响请求处理
      setImmediate(() => {
        fs.appendFile(this.logFile, logLine, (err) => {
          if (err) {
            console.error('写入性能日志失败:', err);
          }
        });
      });
    } catch (error) {
      console.error('记录性能数据失败:', error);
    }
  }

  /**
   * 获取性能统计信息
   */
  getStats() {
    const avgResponseTime =
      this.metrics.requests > 0 ? this.metrics.totalResponseTime / this.metrics.requests : 0;

    const cacheHitRate =
      this.metrics.cacheHits + this.metrics.cacheMisses > 0
        ? (this.metrics.cacheHits / (this.metrics.cacheHits + this.metrics.cacheMisses)) * 100
        : 0;

    const endpointStats = [];
    for (const [endpoint, metrics] of this.metrics.endpoints) {
      const avgTime = metrics.requests > 0 ? metrics.totalTime / metrics.requests : 0;
      const hitRate =
        metrics.cacheHits + metrics.cacheMisses > 0
          ? (metrics.cacheHits / (metrics.cacheHits + metrics.cacheMisses)) * 100
          : 0;

      endpointStats.push({
        endpoint,
        requests: metrics.requests,
        avgResponseTime: Math.round(avgTime),
        cacheHitRate: Math.round(hitRate * 100) / 100,
        errors: metrics.errors
      });
    }

    // 按请求数排序
    endpointStats.sort((a, b) => b.requests - a.requests);

    return {
      totalRequests: this.metrics.requests,
      avgResponseTime: Math.round(avgResponseTime),
      cacheHitRate: Math.round(cacheHitRate * 100) / 100,
      totalCacheHits: this.metrics.cacheHits,
      totalCacheMisses: this.metrics.cacheMisses,
      endpoints: endpointStats
    };
  }

  /**
   * 生成性能报告
   */
  generateReport() {
    const stats = this.getStats();
    const timestamp = new Date().toISOString();

    // 生成CSV格式的报告
    const csvHeader = 'timestamp,endpoint,requests,avg_response_time,cache_hit_rate,errors\n';
    let csvContent = csvHeader;

    for (const endpoint of stats.endpoints) {
      csvContent += `${timestamp},"${endpoint.endpoint}",${endpoint.requests},${endpoint.avgResponseTime},${endpoint.cacheHitRate},${endpoint.errors}\n`;
    }

    // 写入CSV文件
    fs.writeFile(this.reportFile, csvContent, (err) => {
      if (err) {
        console.error('生成性能报告失败:', err);
      } else {
        console.log('性能报告已生成:', this.reportFile);
      }
    });

    // 生成Markdown格式的报告
    const markdownReport = this.generateMarkdownReport(stats);
    const markdownFile = path.join(process.cwd(), 'test-results', 'performance_report.md');

    fs.writeFile(markdownFile, markdownReport, (err) => {
      if (err) {
        console.error('生成Markdown报告失败:', err);
      } else {
        console.log('Markdown性能报告已生成:', markdownFile);
      }
    });

    return stats;
  }

  /**
   * 生成Markdown格式的性能报告
   */
  generateMarkdownReport(stats) {
    const timestamp = new Date().toLocaleString('zh-CN');

    let markdown = '# 系统性能监控报告\n\n';
    markdown += `**生成时间:** ${timestamp}\n\n`;

    markdown += '## 总体性能指标\n\n';
    markdown += `- **总请求数:** ${stats.totalRequests}\n`;
    markdown += `- **平均响应时间:** ${stats.avgResponseTime}ms\n`;
    markdown += `- **缓存命中率:** ${stats.cacheHitRate}%\n`;
    markdown += `- **缓存命中次数:** ${stats.totalCacheHits}\n`;
    markdown += `- **缓存未命中次数:** ${stats.totalCacheMisses}\n\n`;

    markdown += '## 接口性能详情\n\n';
    markdown += '| 接口 | 请求数 | 平均响应时间(ms) | 缓存命中率(%) | 错误数 |\n';
    markdown += '|------|--------|------------------|---------------|--------|\n';

    for (const endpoint of stats.endpoints) {
      markdown += `| ${endpoint.endpoint} | ${endpoint.requests} | ${endpoint.avgResponseTime} | ${endpoint.cacheHitRate} | ${endpoint.errors} |\n`;
    }

    markdown += '\n## 性能优化建议\n\n';

    if (stats.cacheHitRate < 50) {
      markdown += `- ⚠️ 缓存命中率较低(${stats.cacheHitRate}%)，建议检查缓存策略\n`;
    } else if (stats.cacheHitRate > 80) {
      markdown += `- ✅ 缓存命中率良好(${stats.cacheHitRate}%)\n`;
    }

    if (stats.avgResponseTime > 1000) {
      markdown += `- ⚠️ 平均响应时间较长(${stats.avgResponseTime}ms)，建议优化查询或增加缓存\n`;
    } else if (stats.avgResponseTime < 200) {
      markdown += `- ✅ 响应时间表现良好(${stats.avgResponseTime}ms)\n`;
    }

    // 找出响应时间最长的接口
    if (stats.endpoints && stats.endpoints.length > 0) {
      const slowestEndpoint = stats.endpoints.reduce((prev, current) =>
        prev.avgResponseTime > current.avgResponseTime ? prev : current
      );

      if (slowestEndpoint && slowestEndpoint.avgResponseTime > 500) {
        markdown += `- ⚠️ 最慢接口: ${slowestEndpoint.endpoint} (${slowestEndpoint.avgResponseTime}ms)，建议优化\n`;
      }
    }

    return markdown;
  }

  /**
   * 开始定期报告
   */
  startPeriodicReporting() {
    // 延迟启动报告生成，避免在应用启动时就开始生成报告
    setTimeout(() => {
      // 每5分钟生成一次报告
      setInterval(
        () => {
          try {
            this.generateReport();
          } catch (error) {
            console.error('生成性能报告失败:', error);
          }
        },
        5 * 60 * 1000
      );

      // 每小时输出一次统计信息到控制台
      setInterval(
        () => {
          try {
            const stats = this.getStats();
            console.log('\n=== 性能监控统计 ===');
            console.log(`总请求数: ${stats.totalRequests}`);
            console.log(`平均响应时间: ${stats.avgResponseTime}ms`);
            console.log(`缓存命中率: ${stats.cacheHitRate}%`);
            console.log('==================\n');
          } catch (error) {
            console.error('输出性能统计信息失败:', error);
          }
        },
        60 * 60 * 1000
      );
    }, 60 * 1000); // 延迟1分钟启动
  }

  /**
   * 重置统计数据
   */
  reset() {
    this.metrics = {
      requests: 0,
      totalResponseTime: 0,
      cacheHits: 0,
      cacheMisses: 0,
      endpoints: new Map()
    };
  }
}

// 创建全局性能监控实例
const performanceMonitor = new PerformanceMonitor();

export { performanceMonitor, performanceMonitor as default };

export const performanceMiddleware = performanceMonitor.middleware.bind(performanceMonitor);

/**
 * 响应格式化中间件
 * 提供统一的API响应格式
 */

class ResponseFormatter {
  /**
   * 成功响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 响应消息
   * @param {*} data - 响应数据
   * @param {number} statusCode - HTTP状态码
   */
  static success(res, message = '操作成功', data = null, statusCode = 200) {
    const response = {
      success: true,
      message,
      timestamp: new Date().toISOString(),
      statusCode
    };

    if (data !== null) {
      response.data = data;
    }

    return res.status(statusCode).json(response);
  }

  /**
   * 错误响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 错误消息
   * @param {number} statusCode - HTTP状态码
   * @param {*} errors - 详细错误信息
   */
  static error(res, message = '操作失败', statusCode = 500, errors = null) {
    const response = {
      success: false,
      message,
      timestamp: new Date().toISOString(),
      statusCode
    };

    if (errors !== null) {
      response.errors = errors;
    }

    // 记录错误日志
    if (statusCode >= 500) {
      console.error(`[${new Date().toISOString()}] Server Error:`, {
        message,
        statusCode,
        errors
      });
    }

    return res.status(statusCode).json(response);
  }

  /**
   * 分页响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 响应消息
   * @param {Array} data - 数据数组
   * @param {Object} pagination - 分页信息
   */
  static paginated(res, message = '获取数据成功', data = [], pagination = {}) {
    const response = {
      success: true,
      message,
      timestamp: new Date().toISOString(),
      statusCode: 200,
      data,
      pagination: {
        page: pagination.page || 1,
        limit: pagination.limit || 20,
        total: pagination.total || 0,
        pages: pagination.pages || 0,
        hasNext: pagination.hasNext || false,
        hasPrev: pagination.hasPrev || false
      }
    };

    return res.status(200).json(response);
  }

  /**
   * 验证错误响应
   * @param {Object} res - Express响应对象
   * @param {Array} validationErrors - 验证错误数组
   */
  static validationError(res, validationErrors) {
    const formattedErrors = validationErrors.map((error) => ({
      field: error.param || error.path,
      message: error.msg || error.message,
      value: error.value
    }));

    return this.error(res, '输入验证失败', 400, formattedErrors);
  }

  /**
   * 未授权响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 错误消息
   */
  static unauthorized(res, message = '未授权访问') {
    return this.error(res, message, 401);
  }

  /**
   * 禁止访问响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 错误消息
   */
  static forbidden(res, message = '禁止访问') {
    return this.error(res, message, 403);
  }

  /**
   * 资源不存在响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 错误消息
   */
  static notFound(res, message = '资源不存在') {
    return this.error(res, message, 404);
  }

  /**
   * 冲突响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 错误消息
   */
  static conflict(res, message = '资源冲突') {
    return this.error(res, message, 409);
  }

  /**
   * 请求过于频繁响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 错误消息
   * @param {number} retryAfter - 重试等待时间（秒）
   */
  static tooManyRequests(res, message = '请求过于频繁', retryAfter = 60) {
    res.setHeader('Retry-After', retryAfter);
    return this.error(res, message, 429);
  }

  /**
   * 服务器内部错误响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 错误消息
   * @param {Error} error - 错误对象
   */
  static internalError(res, message = '服务器内部错误', error = null) {
    // 在开发环境中包含错误堆栈
    const isDevelopment = process.env.NODE_ENV === 'development';
    const errorDetails =
      isDevelopment && error
        ? {
          stack: error.stack,
          name: error.name
        }
        : null;

    return this.error(res, message, 500, errorDetails);
  }

  /**
   * 自定义状态码响应
   * @param {Object} res - Express响应对象
   * @param {number} statusCode - HTTP状态码
   * @param {string} message - 响应消息
   * @param {*} data - 响应数据
   */
  static custom(res, statusCode, message, data = null) {
    const isSuccess = statusCode >= 200 && statusCode < 300;

    if (isSuccess) {
      return this.success(res, message, data, statusCode);
    } 
    return this.error(res, message, statusCode, data);

  }

  /**
   * 文件下载响应
   * @param {Object} res - Express响应对象
   * @param {Buffer|string} fileData - 文件数据
   * @param {string} filename - 文件名
   * @param {string} contentType - 内容类型
   */
  static fileDownload(res, fileData, filename, contentType = 'application/octet-stream') {
    res.setHeader('Content-Type', contentType);
    res.setHeader('Content-Disposition', `attachment; filename="${filename}"`);
    res.setHeader('Content-Length', Buffer.byteLength(fileData));

    return res.send(fileData);
  }

  /**
   * 流式响应
   * @param {Object} res - Express响应对象
   * @param {Stream} stream - 数据流
   * @param {string} contentType - 内容类型
   */
  static stream(res, stream, contentType = 'application/octet-stream') {
    res.setHeader('Content-Type', contentType);
    res.setHeader('Transfer-Encoding', 'chunked');

    stream.pipe(res);

    stream.on('error', (error) => {
      console.error('Stream error:', error);
      if (!res.headersSent) {
        this.internalError(res, '数据流传输错误', error);
      }
    });
  }

  /**
   * 缓存响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 响应消息
   * @param {*} data - 响应数据
   * @param {number} maxAge - 缓存时间（秒）
   */
  static cached(res, message, data, maxAge = 3600) {
    res.setHeader('Cache-Control', `public, max-age=${maxAge}`);
    res.setHeader('ETag', this._generateETag(data));

    return this.success(res, message, data);
  }

  /**
   * 生成ETag
   * @param {*} data - 数据
   * @returns {string} ETag值
   * @private
   */
  static _generateETag(data) {
    const crypto = require('crypto');
    const content = typeof data === 'string' ? data : JSON.stringify(data);
    return crypto.createHash('md5').update(content).digest('hex');
  }

  /**
   * 健康检查响应
   * @param {Object} res - Express响应对象
   * @param {Object} healthData - 健康状态数据
   */
  static health(res, healthData = {}) {
    const response = {
      status: 'healthy',
      timestamp: new Date().toISOString(),
      uptime: process.uptime(),
      memory: process.memoryUsage(),
      ...healthData
    };

    return res.status(200).json(response);
  }

  /**
   * API版本不支持响应
   * @param {Object} res - Express响应对象
   * @param {string} requestedVersion - 请求的版本
   * @param {Array} supportedVersions - 支持的版本列表
   */
  static unsupportedVersion(res, requestedVersion, supportedVersions = []) {
    return this.error(res, `不支持的API版本: ${requestedVersion}`, 400, {
      requestedVersion,
      supportedVersions
    });
  }

  /**
   * 维护模式响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 维护消息
   * @param {Date} estimatedEnd - 预计结束时间
   */
  static maintenance(res, message = '系统正在维护中', estimatedEnd = null) {
    const response = {
      success: false,
      message,
      timestamp: new Date().toISOString(),
      statusCode: 503,
      maintenance: true
    };

    if (estimatedEnd) {
      response.estimatedEnd = estimatedEnd.toISOString();
      res.setHeader('Retry-After', Math.ceil((estimatedEnd - new Date()) / 1000));
    }

    return res.status(503).json(response);
  }
}

// 中间件函数，将ResponseFormatter添加到res对象
const responseFormatterMiddleware = (req, res, next) => {
  // 将所有静态方法绑定到res对象
  res.success = (message, data, statusCode) =>
    ResponseFormatter.success(res, message, data, statusCode);
  res.error = (message, statusCode, errors) =>
    ResponseFormatter.error(res, message, statusCode, errors);
  res.paginated = (message, data, pagination) =>
    ResponseFormatter.paginated(res, message, data, pagination);
  res.validationError = (validationErrors) =>
    ResponseFormatter.validationError(res, validationErrors);
  res.unauthorized = (message) => ResponseFormatter.unauthorized(res, message);
  res.forbidden = (message) => ResponseFormatter.forbidden(res, message);
  res.notFound = (message) => ResponseFormatter.notFound(res, message);
  res.conflict = (message) => ResponseFormatter.conflict(res, message);
  res.tooManyRequests = (message, retryAfter) =>
    ResponseFormatter.tooManyRequests(res, message, retryAfter);
  res.internalError = (message, error) => ResponseFormatter.internalError(res, message, error);
  res.custom = (statusCode, message, data) =>
    ResponseFormatter.custom(res, statusCode, message, data);
  res.fileDownload = (fileData, filename, contentType) =>
    ResponseFormatter.fileDownload(res, fileData, filename, contentType);
  res.stream = (stream, contentType) => ResponseFormatter.stream(res, stream, contentType);
  res.cached = (message, data, maxAge) => ResponseFormatter.cached(res, message, data, maxAge);
  res.health = (healthData) => ResponseFormatter.health(res, healthData);
  res.unsupportedVersion = (requestedVersion, supportedVersions) =>
    ResponseFormatter.unsupportedVersion(res, requestedVersion, supportedVersions);
  res.maintenance = (message, estimatedEnd) =>
    ResponseFormatter.maintenance(res, message, estimatedEnd);

  next();
};

module.exports = ResponseFormatter;
module.exports.middleware = responseFormatterMiddleware;

