  body('type').isIn(DIGITAL_TWIN_CONSTANTS.VIEW_PRESETS).withMessage('视图类型无效'),
  body('cameraPosition').isObject().withMessage('相机位置必须是对象'),
  body('cameraTarget').isObject().withMessage('相机目标必须是对象'),
