// 错误消息映射
const ERROR_MESSAGES = {
  [BUSINESS_CODES.AUTH_INVALID_CREDENTIALS]: '用户名或密码错误',
  [BUSINESS_CODES.AUTH_TOKEN_EXPIRED]: '访问令牌已过期',
