  [BUSINESS_CODES.VALIDATION_INVALID_FORMAT]: '数据格式无效',
  [BUSINESS_CODES.VALIDATION_OUT_OF_RANGE]: '数据超出允许范围',

  [BUSINESS_CODES.RESOURCE_NOT_FOUND]: '资源未找到',
  [BUSINESS_CODES.RESOURCE_ALREADY_EXISTS]: '资源已存在',
