const express = require('express');
const router = express.Router();
const energyPredictionService = require('../../services/energyPredictionService');
const { authenticate } = require('../../middleware/auth');
const logger = require('../../utils/logger');
const { check, validationResult } = require('express-validator');

/**
 * @route   POST /api/energy/predict
 * @desc    预测特定时间的能源消耗
 * @access  Private
 */
router.post(
  '/predict',
  [
    authenticate,
    [
      check('hour', '小时必须是0-23之间的数字').isInt({ min: 0, max: 23 }),
      check('dayOfWeek', '星期必须是0-6之间的数字').isInt({ min: 0, max: 6 }),
      check('temperature', '温度必须是有效的数字').optional().isFloat(),
      check('humidity', '湿度必须是0-100之间的数字').optional().isFloat({ min: 0, max: 100 }),
      check('previousEnergyUsage', '前一小时能耗必须是正数').optional().isFloat({ min: 0 }),
    ],
  ],
  async (req, res) => {
    try {
      // 验证请求参数
      const errors = validationResult(req);
      if (!errors.isEmpty()) {
        return res.status(400).json({ errors: errors.array() });
      }

      // 调用预测服务
      const prediction = await energyPredictionService.predictEnergyUsage(req.body);

      if (prediction === null) {
        return res.status(500).json({
          message: '能源消耗预测失败，请稍后重试',
        });
      }

      // 返回预测结果
      res.json({
        success: true,
        predictedEnergyUsage: parseFloat(prediction.toFixed(2)),
        unit: 'kWh',
      });
    } catch (error) {
      logger.error('能源预测API错误:', error);
      res.status(500).json({
        message: '服务器错误，无法完成预测',
      });
    }
  }
);

/**
 * @route   GET /api/energy/predict/24hours
 * @desc    预测未来24小时的能源消耗
 * @access  Private
 */
router.get('/predict/24hours', authenticateToken(), async (req, res) => {
  try {
    // 获取查询参数中的温度和湿度（可选）
    const { temperature, humidity } = req.query;
    const baseData = {};

    if (temperature) {
      baseData.temperature = parseFloat(temperature);
    }
    if (humidity) {
      baseData.humidity = parseFloat(humidity);
    }

    // 调用预测服务
    const predictions = await energyPredictionService.predict24Hours(baseData);

    if (predictions.length === 0) {
      return res.status(500).json({
        message: '24小时能源预测失败，请稍后重试',
      });
    }

    // 格式化预测结果
    const formattedPredictions = predictions.map((pred) => ({
      hour: pred.hour,
      predictedEnergyUsage: parseFloat(pred.predictedEnergyUsage.toFixed(2)),
      timestamp: pred.timestamp,
      unit: 'kWh',
    }));

    // 返回预测结果
    res.json({
      success: true,
      predictions: formattedPredictions,
      generatedAt: new Date(),
    });
  } catch (error) {
    logger.error('24小时能源预测API错误:', error);
    res.status(500).json({
      message: '服务器错误，无法完成24小时预测',
    });
  }
});

/**
 * @route   GET /api/energy/predict/model/status
 * @desc    获取预测模型状态
 * @access  Private
 */
router.get('/predict/model/status', authenticateToken(), async (req, res) => {
  try {
    // 返回模型状态信息
    res.json({
      success: true,
      isModelTrained: energyPredictionService.isModelTrained,
      lastTrained: new Date().toISOString(), // 在实际应用中应该存储和返回真实的最后训练时间
      features: energyPredictionService.features,
    });
  } catch (error) {
    logger.error('模型状态API错误:', error);
    res.status(500).json({
      message: '服务器错误，无法获取模型状态',
    });
  }
});

/**
 * @route   POST /api/energy/predict/model/retrain
 * @desc    重新训练预测模型
 * @access  Private (Admin only)
 */
router.post(
  '/predict/model/retrain',
  [
    authenticateToken(),
    // 在实际应用中应该添加管理员权限检查
    [
      check('epochs', '训练轮次必须是正整数').optional().isInt({ min: 1 }),
      check('batchSize', '批次大小必须是正整数').optional().isInt({ min: 1 }),
    ],
  ],
  async (req, res) => {
    try {
      // 验证请求参数
      const errors = validationResult(req);
      if (!errors.isEmpty()) {
        return res.status(400).json({ errors: errors.array() });
      }

      // 异步执行模型训练，不阻塞响应
      const { epochs, batchSize } = req.body;
      energyPredictionService
        .trainModel(epochs, batchSize)
        .then(() => {
          logger.info('模型重新训练成功');
        })
        .catch((err) => {
          logger.error('模型重新训练失败:', err);
        });

      // 立即返回响应，告知训练已开始
      res.json({
        success: true,
        message: '模型重新训练已开始，请稍后查看结果',
      });
    } catch (error) {
      logger.error('模型重新训练API错误:', error);
      res.status(500).json({
        message: '服务器错误，无法启动模型训练',
      });
    }
  }
);

module.exports = router;

const energyPredictionService = require('../services/energyPredictionService');
const logger = require('../utils/logger');
const { program } = require('commander');

/**
 * 能源预测模型训练脚本
 * 用于手动触发或定时执行模型训练
 */
async function trainModel() {
  try {
    // 解析命令行参数
    program
      .option('-e, --epochs <number>', '训练轮次', parseInt, 50)
      .option('-b, --batch-size <number>', '批次大小', parseInt, 32)
      .option('-l, --log-level <level>', '日志级别', 'info')
      .parse(process.argv);

    const options = program.opts();

    // 设置日志级别
    logger.level = options.logLevel;

    logger.info(`开始训练能源预测模型 - 轮次: ${options.epochs}, 批次大小: ${options.batchSize}`);

    // 训练模型
    const startTime = Date.now();
    const trainingSuccess = await energyPredictionService.trainModel(
      options.epochs,
      options.batchSize
    );
    const endTime = Date.now();

    if (trainingSuccess) {
      logger.info(`模型训练成功，耗时: ${Math.round((endTime - startTime) / 1000)}秒`);
      process.exit(0);
    } else {
      logger.error('模型训练失败');
      process.exit(1);
    }
  } catch (error) {
    logger.error('模型训练脚本执行失败:', error);
    process.exit(1);
  }
}

// 执行训练
if (require.main === module) {
  trainModel();
}

module.exports = trainModel;

import logger from '../../src/shared/utils/logger.js';
import Device from '../../src/core/entities/Device.js';
import { dbPromise } from '../../src/infrastructure/database/index.js';

class AlertService {
  constructor() {
    // 可以初始化一些告警规则或阈值
    this.alertRules = [
      { type: 'energy_threshold', threshold: 1000, message: '设备能耗超出阈值', severity: 'high' },
      { type: 'device_offline', message: '设备离线', severity: 'critical' },
      // ... 更多告警规则
    ];
  }

  /**
   * 检查并生成告警
   * @param {string} type - 告警类型 (e.g., 'energy_threshold', 'device_offline')
   * @param {object} data - 告警相关数据 (e.g., { deviceId: 'xxx', currentConsumption: 1200 })
   * @returns {Promise<Alert|null>} - 生成的告警对象或 null
   */
  async checkAndGenerateAlert(type, data) {
    const rule = this.alertRules.find((r) => r.type === type);
    if (!rule) {
      logger.warn(`未找到告警类型 '${type}' 的规则。`);
      return null;
    }

    let shouldAlert = false;
    let alertMessage = rule.message;

    switch (type) {
      case 'energy_threshold':
        if (data.currentConsumption > rule.threshold) {
          shouldAlert = true;
          alertMessage = `${rule.message}: 设备 ${data.deviceId} 当前能耗 ${data.currentConsumption} kWh，超过阈值 ${rule.threshold} kWh。`;
        }
        break;
      case 'device_offline':
        // 假设 data.deviceId 存在且设备确实离线
        shouldAlert = true;
        alertMessage = `${rule.message}: 设备 ${data.deviceId} 已离线。`;
        break;
      // ... 更多告警类型判断
    }

    if (shouldAlert) {
      try {
        const db = await dbPromise;
        const newAlert = await db('alerts')
          .insert({
            device_id: data.deviceId || null,
            type: rule.type,
            message: alertMessage,
            severity: rule.severity,
            timestamp: new Date().toISOString(), // Store as ISO string
            status: 'active', // Initial status is active
          })
          .returning('*'); // Return the inserted row(s)
        logger.info(`生成新告警: [${rule.severity}] ${alertMessage}`);
        return newAlert;
      } catch (error) {
        logger.error('生成告警失败:', error);
        return null;
      }
    }
    return null;
  }

  /**
   * 获取所有活跃告警
   * @returns {Promise<Array<Alert>>} - 活跃告警列表
   */
  async getActiveAlerts() {
    try {
      const db = await dbPromise;
      const alerts = await db('alerts').where({ status: 'active' });
      // Note: Knex does not have a direct 'include' equivalent like Sequelize.
      // If device information is needed, it's recommended to perform a separate query or join.
      // For now, we'll just return the alerts.
      return alerts;
    } catch (error) {
      logger.error('获取活跃告警失败:', error);
      return [];
    }
  }

  /**
   * 更新告警状态
   * @param {string} alertId - 告警ID
   * @param {string} newStatus - 新状态 (e.g., 'resolved', 'acknowledged')
   * @returns {Promise<boolean>} - 更新是否成功
   */
  async updateAlertStatus(alertId, newStatus) {
    try {
      const db = await dbPromise;
      const updatedRows = await db('alerts').where({ id: alertId }).update({ status: newStatus });
      if (updatedRows > 0) {
        logger.info(`告警 ${alertId} 状态更新为 '${newStatus}'。`);
        return true;
      }
      logger.warn(`未找到告警 ${alertId} 或状态未改变。`);
      return false;
    } catch (error) {
      logger.error(`更新告警 ${alertId} 状态失败:`, error);
      return false;
    }
  }

  /**
   * 添加或更新告警规则
   * @param {object} rule - 告警规则对象
   */
  addOrUpdateAlertRule(rule) {
    const index = this.alertRules.findIndex((r) => r.type === rule.type);
    if (index !== -1) {
      this.alertRules[index] = { ...this.alertRules[index], ...rule };
      logger.info(`告警规则 '${rule.type}' 已更新。`);
    } else {
      this.alertRules.push(rule);
      logger.info(`新告警规则 '${rule.type}' 已添加。`);
    }
  }

  /**
   * 移除告警规则
   * @param {string} type - 告警类型
   * @returns {boolean} - 移除是否成功
   */
  removeAlertRule(type) {
    const initialLength = this.alertRules.length;
    this.alertRules = this.alertRules.filter((r) => r.type !== type);
    if (this.alertRules.length < initialLength) {
      logger.info(`告警规则 '${type}' 已移除。`);
      return true;
    }
    logger.warn(`未找到告警规则 '${type}'。`);
    return false;
  }
}

export default AlertService;

import logger from '../../src/shared/utils/logger.js';
// import { EnergyData } from '../models/index.js'; // 暂时注释掉，使用数据库直接查询

class CarbonEmissionService {
  constructor() {
    // 假设的排放因子 (kg CO2e / kWh)
    // 实际应用中这些因子应从数据库或配置中获取，并根据能源类型、地区等细化
    this.emissionFactors = {
      electricity: 0.5, // 假设电力的平均排放因子
      naturalGas: 0.2, // 假设天然气的排放因子
      // ... 其他能源类型
    };
  }

  /**
   * 计算指定能源消耗的碳排放量
   * @param {string} energyType - 能源类型 (e.g., 'electricity', 'naturalGas')
   * @param {number} energyConsumption - 能源消耗量 (kWh 或其他单位)
   * @returns {number} - 碳排放量 (kg CO2e)
   */
  calculateEmission(energyType, energyConsumption) {
    const factor = this.emissionFactors[energyType];
    if (factor === undefined) {
      logger.warn(`未找到能源类型 '${energyType}' 的排放因子，将返回 0。`);
      return 0;
    }
    return energyConsumption * factor;
  }

  /**
   * 根据一段时间内的能源数据计算总碳排放量
   * @param {Date} startTime - 开始时间
   * @param {Date} endTime - 结束时间
   * @returns {Promise<number>} - 总碳排放量 (kg CO2e)
   */
  async calculateTotalEmissions(startTime, endTime) {
    try {
      // 暂时使用模拟数据，避免数据库查询错误
      logger.warn('使用模拟数据计算碳排放，实际应用中需要连接真实数据库');

      // 生成模拟的能源记录
      const energyRecords = [];
      const timeDiff = endTime.getTime() - startTime.getTime();
      const hours = Math.floor(timeDiff / (1000 * 60 * 60));

      for (let i = 0; i < Math.min(hours, 100); i++) {
        energyRecords.push({
          energyType: 'electricity',
          energyUsage: 500 + Math.random() * 500, // 500-1000 kWh
          timestamp: new Date(startTime.getTime() + i * 60 * 60 * 1000),
        });
      }

      let totalEmissions = 0;
      for (const record of energyRecords) {
        // 假设 EnergyData 记录包含 energyType 和 energyUsage 字段
        // 实际应用中需要根据数据模型调整
        totalEmissions += this.calculateEmission(record.energyType, record.energyUsage);
      }
      logger.info(
        `计算 ${startTime.toISOString()} 到 ${endTime.toISOString()} 期间的总碳排放量: ${totalEmissions.toFixed(2)} kg CO2e`
      );
      return totalEmissions;
    } catch (error) {
      logger.error('计算总碳排放量失败:', error);
      return 0;
    }
  }

  /**
   * 获取并更新排放因子 (模拟)
   * 实际中可能从外部API或数据库同步
   * @param {object} newFactors - 新的排放因子对象
   */
  updateEmissionFactors(newFactors) {
    Object.assign(this.emissionFactors, newFactors);
    logger.info('排放因子已更新。');
  }
}

export default CarbonEmissionService;

import tf from '@tensorflow/tfjs-node';
// import { EnergyData } from '../models/index.js'; // 暂时注释掉，使用数据库直接查询
import logger from '../../src/shared/utils/logger.js';

class EnergyPredictionService {
  constructor() {
    this.model = null;
    this.isModelTrained = false;
    this.features = ['hour', 'dayOfWeek', 'temperature', 'humidity', 'previousEnergyUsage'];
    this.target = 'energyUsage';
    this.initializeModel();
  }

  /**
   * 初始化预测模型
   */
  async initializeModel() {
    try {
      // 创建一个简单的神经网络模型
      this.model = tf.sequential({
        layers: [
          tf.layers.dense({
            inputShape: [this.features.length],
            units: 32,
            activation: 'relu',
            kernelInitializer: 'heNormal',
          }),
          tf.layers.dropout({ rate: 0.2 }),
          tf.layers.dense({ units: 16, activation: 'relu' }),
          tf.layers.dense({ units: 8, activation: 'relu' }),
          tf.layers.dense({ units: 1 }),
        ],
      });

      // 编译模型
      this.model.compile({
        optimizer: tf.train.adam(0.001),
        loss: 'meanSquaredError',
        metrics: ['mse'],
      });

      logger.info('能源预测模型初始化成功');

      // 尝试加载已保存的模型
      await this.loadModel();
    } catch (error) {
      logger.error('能源预测模型初始化失败:', error);
    }
  }

  /**
   * 从数据库获取训练数据
   * @param {number} limit - 获取数据条数
   * @returns {Promise<{features: number[][], labels: number[]}>} - 特征和标签数据
   */
  async fetchTrainingData(limit = 10000) {
    try {
      // 暂时返回模拟数据，避免数据库查询错误
      logger.warn('使用模拟数据进行训练，实际应用中需要连接真实数据库');

      // 生成模拟的能源数据
      const energyData = [];
      const now = new Date();

      for (let i = 0; i < Math.min(limit, 1000); i++) {
        const timestamp = new Date(now.getTime() - i * 60 * 60 * 1000); // 每小时一条数据
        energyData.push({
          timestamp: timestamp.toISOString(),
          energyUsage: 500 + Math.random() * 500, // 500-1000 kWh
          temperature: 20 + Math.random() * 20, // 20-40°C
          humidity: 40 + Math.random() * 40, // 40-80%
        });
      }

      if (energyData.length < 100) {
        logger.warn('训练数据不足，需要至少100条记录');
        return null;
      }

      // 数据预处理
      const processedData = this.preprocessData(energyData);
      return processedData;
    } catch (error) {
      logger.error('获取训练数据失败:', error);
      return null;
    }
  }

  /**
   * 数据预处理
   * @param {Array} data - 原始数据
   * @returns {Object} - 处理后的特征和标签
   */
  preprocessData(data) {
    // 按时间戳排序
    const sortedData = [...data].sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));

    const features = [];
    const labels = [];

    // 提取特征和标签

    // TODO: 考虑将此函数拆分为更小的函数 (当前 21 行)

    // TODO: 考虑将此函数拆分为更小的函数 (当前 21 行)

    // TODO: 考虑将此函数拆分为更小的函数 (当前 21 行)

    // TODO: 考虑将此函数拆分为更小的函数 (当前 21 行)

    for (let i = 1; i < sortedData.length; i++) {
      const current = sortedData[i];
      const previous = sortedData[i - 1];
      const date = new Date(current.timestamp);

      // 提取时间特征
      const hour = date.getHours();
      const dayOfWeek = date.getDay();

      // 添加特征
      features.push([
        hour / 23, // 归一化到0-1
        dayOfWeek / 6, // 归一化到0-1
        (current.temperature || 20) / 40, // 假设温度范围0-40
        (current.humidity || 50) / 100, // 湿度0-100
        previous.energyUsage / 1000, // 前一小时能耗，假设最大值1000
      ]);

      // 添加标签
      labels.push([current.energyUsage]);
    }

    return { features, labels };
  }

  /**
   * 训练模型
   * @param {number} epochs - 训练轮次
   * @param {number} batchSize - 批次大小
   * @returns {Promise<boolean>} - 训练是否成功
   */
  async trainModel(epochs = 50, batchSize = 32) {
    try {
      logger.info('开始训练能源预测模型...');

      // 获取训练数据
      const trainingData = await this.fetchTrainingData();
      if (!trainingData) {
        return false;
      }

      // 转换为张量
      const xs = tf.tensor2d(trainingData.features);
      const ys = tf.tensor2d(trainingData.labels);

      // 训练模型
      const history = await this.model.fit(xs, ys, {
        epochs,
        batchSize,
        validationSplit: 0.2,
        shuffle: true,
        callbacks: {
          onEpochEnd: (epoch, logs) => {
            logger.info(
              `Epoch ${epoch + 1}/${epochs} - loss: ${logs.loss.toFixed(4)} - val_loss: ${logs.val_loss.toFixed(4)}`
            );
          },
        },
      });

      // 释放张量内存
      xs.dispose();
      ys.dispose();

      const finalLoss = history.history.loss[history.history.loss.length - 1];
      const finalValLoss = history.history.val_loss[history.history.val_loss.length - 1];

      logger.info(
        `模型训练完成 - 最终损失: ${finalLoss.toFixed(4)} - 最终验证损失: ${finalValLoss.toFixed(4)}`
      );

      this.isModelTrained = true;

      // 保存模型
      await this.saveModel();

      return true;
    } catch (error) {
      logger.error('模型训练失败:', error);
      return false;
    }
  }

  /**
   * 保存模型到文件系统
   * @returns {Promise<void>}
   */
  async saveModel() {
    try {
      await this.model.save('file://./models/energy-prediction-model');
      logger.info('模型保存成功');
    } catch (error) {
      logger.error('模型保存失败:', error);
    }
  }

  /**
   * 从文件系统加载模型
   * @returns {Promise<boolean>}
   */
  async loadModel() {
    try {
      this.model = await tf.loadLayersModel('file://./models/energy-prediction-model/model.json');
      this.isModelTrained = true;
      logger.info('模型加载成功');
      return true;
    } catch (error) {
      logger.warn('模型加载失败，将使用新模型:', error.message);
      return false;
    }
  }

  /**
   * 预测能源消耗
   * @param {Object} inputData - 输入特征数据
   * @returns {Promise<number>} - 预测的能源消耗量
   */
  async predictEnergyUsage(inputData) {
    try {
      if (!this.isModelTrained) {
        logger.warn('模型尚未训练，正在进行首次训练...');
        const trainingSuccess = await this.trainModel();
        if (!trainingSuccess) {
          logger.error('模型训练失败，无法进行预测');
          return null;
        }
      }

      // 准备输入特征
      const inputFeatures = [
        inputData.hour / 23,
        inputData.dayOfWeek / 6,
        (inputData.temperature || 20) / 40,
        (inputData.humidity || 50) / 100,
        (inputData.previousEnergyUsage || 0) / 1000,
      ];

      // 转换为张量并进行预测
      const inputTensor = tf.tensor2d([inputFeatures]);
      const prediction = this.model.predict(inputTensor);
