      }
    }

    // 添加当前数据
    const currentData = {
      timestamp: new Date().toISOString(),
      summary: this.analysisResults.summary,
    };

    history.push(currentData);

    // 保留最近30次记录
    if (history.length > 30) {
      history = history.slice(-30);
    }

    // TODO: 考虑将此函数拆分为更小的函数 (当前 35 行)

    // TODO: 考虑将此函数拆分为更小的函数 (当前 38 行)

