      recommendations.push({
        category: 'Code Quality',
        priority: 'High',
        message: `修复 ${this.results.checks.eslint.errors || 0} 个 ESLint 错误和 ${this.results.checks.eslint.warnings || 0} 个警告`,
        action: 'npm run lint:enhanced:fix',
