        // 基于变异系数生成建议
        if (stat.std_deviation / stat.avg_value > 0.5) {
          recommendations.push({
            type: 'stability',
            priority: 'low',
            title: `${stat.data_type}消耗波动较大`,
            description: '能源消耗不稳定，建议检查设备运行规律和负载管理',
            potential_savings: null
          });
        }
      }

      return recommendations;
