      }
    }, interval);
  }

  // 判断是否需要执行优化
  shouldExecuteOptimization(metrics) {
    // 简单的优化触发条件
    return metrics.efficiency < 70 || metrics.renewableRatio < 30 || metrics.loadFactor < 50;
  }

  // 停止监控
  stopMonitoring() {
    if (this.monitoringInterval) {
      clearInterval(this.monitoringInterval);
      this.monitoringInterval = null;
    }
  }

  // 解析时间范围
  parseTimeRange(timeRange) {
    const unit = timeRange.slice(-1);
    const value = parseInt(timeRange.slice(0, -1));

    switch (unit) {
      case 'h':
        return value * 60 * 60 * 1000;
      case 'd':
        return value * 24 * 60 * 60 * 1000;
      case 'm':
        return value * 60 * 1000;
      default:
        return 60 * 60 * 1000;
    }
  }

  // 获取当前效率指标
  getCurrentMetrics() {
    return this.efficiencyMetrics.get('current');
  }

  // 获取设备列表
  getEnergyDevices() {
    return Array.from(this.energyDevices.values());
  }

  // 清理资源
  dispose() {
    this.stopMonitoring();
    this.removeAllListeners();
    this.energyDevices.clear();
    this.consumptionData.clear();
    this.productionData.clear();
    this.efficiencyMetrics.clear();
  }
}

