import {
  CheckCircle,
  Warning,
  Error,
  Info,
  ArrowForward,
  Refresh,
  Download,
  ThumbsUp,
  ThumbsDown,
} from '@mui/icons-material';
import { formatDistanceToNow } from 'date-fns';
import { zhCN } from 'date-fns/locale';
import axios from 'axios';
import { useTheme } from '@mui/material/styles';

// 推荐类型对应的样式和图标
const recommendationTypes = {
  energy_saving: {
    color: '#4caf50',
    icon: <CheckCircle />,
    label: '节能优化',
  },
  cost_reduction: {
    color: '#2196f3',
    icon: <Info />,
    label: '成本降低',
  },
  comfort_improvement: {
    color: '#ff9800',
    icon: <Warning />,
    label: '舒适度提升',
  },
  maintenance: {
    color: '#f44336',
