        </Alert>
      ) : (
        <Grid container spacing={3}>
          // TODO: 考虑将此函数拆分为更小的函数 (当前 118 行) // TODO: 考虑将此函数拆分为更小的函数
          (当前 118 行) // TODO: 考虑将此函数拆分为更小的函数 (当前 118 行) // TODO:
          考虑将此函数拆分为更小的函数 (当前 118 行)
          {sortedRecommendations.map((recommendation) => {
            const typeConfig = getRecommendationTypeConfig(recommendation.type);
            const isNew =
              new Date(recommendation.generated_at) > new Date(Date.now() - 24 * 60 * 60 * 1000);

            return (
