                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                      <Box sx={{ color: typeConfig.color, mr: 1 }}>{typeConfig.icon}</Box>
                      <Typography variant="subtitle1" component="div" fontWeight="bold">
                        {recommendation.rule_name}
                      </Typography>
                    </Box>

                    <Chip
                      size="small"
