                            onClick={() => rateRecommendation(recommendation.id, -1)}
                          >
                            <ThumbsDown fontSize="small" />
                          </IconButton>
                        </Tooltip>
                      </Box>

                      <Box sx={{ display: 'flex', gap: 1 }}>
                        <Button
                          size="small"
                          variant="outlined"
                          onClick={() => dismissRecommendation(recommendation.id)}
                          disabled={loading || recommendation.status === 'applied'}
                        >
                          忽略
                        </Button>
                        <Button
                          size="small"
                          variant="contained"
                          endIcon={<ArrowForward />}
                          onClick={() => applyRecommendation(recommendation)}
                          disabled={loading || recommendation.status === 'applied'}
                        >
                          {recommendation.status === 'applied' ? '已应用' : '应用'}
                        </Button>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            );
          })}
        </Grid>
      )}
    </Box>
  );
};

export default RecommendationList;

import bcrypt from 'bcrypt';
import jwt from 'jsonwebtoken';
import { validationResult } from 'express-validator';
import BaseController from './BaseController.js';
import User from '../../../core/entities/User.js';
import config from '../../../shared/config/index.js';
import logger from '../../../shared/utils/logger.js';
import { defaultJWTManager } from '../../../core/services/jwtManager.js';

/**
 * 认证控制器
 * 处理用户认证相关的业务逻辑
 */
class AuthController extends BaseController {
  constructor() {
    super();
    this.saltRounds = 12;
    this.jwtManager = defaultJWTManager;
  }

  /**
   * 用户登录
   */
  login = this.asyncHandler(async (req, res) => {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.validationError(errors.array());
    }

    const { username, password } = req.body;

    try {
      // 查找用户
      const user = await User.findByUsername(username);
      if (!user) {
        this.logOperation(req, 'LOGIN_FAILED', { username, reason: 'USER_NOT_FOUND' });
        return res.unauthorized('用户名或密码错误');
      }

      // 验证密码
      const isValidPassword = await bcrypt.compare(password, user.password);
      if (!isValidPassword) {
        this.logOperation(req, 'LOGIN_FAILED', { username, reason: 'INVALID_PASSWORD' });
        return res.unauthorized('用户名或密码错误');
      }

      // 检查用户状态
      if (user.status !== 'active') {
        this.logOperation(req, 'LOGIN_FAILED', { username, reason: 'USER_INACTIVE' });
        return res.forbidden('用户账户已被禁用');
      }

      // 生成JWT令牌
      const tokenPayload = {
        id: user.id,
        username: user.username,
        role: user.role,
        permissions: user.permissions || []
      };

      const accessToken = this.jwtManager.generateToken(tokenPayload);
      const refreshToken = this.jwtManager.generateRefreshToken(tokenPayload);

      // 更新最后登录时间
      await User.updateLastLogin(user.id);

      // 清理敏感数据
      const userResponse = this.sanitizeUser(user);

      this.logOperation(req, 'LOGIN_SUCCESS', { userId: user.id, username });

      res.success(
        {
          user: userResponse,
          tokens: {
            accessToken,
            refreshToken,
            expiresIn: config.jwt.expiresIn
          }
        },
        '登录成功'
      );
    } catch (error) {
      logger.error('登录失败', {
        error: error.message,
        username,
        ip: req.ip
      });
      res.internalError('登录失败，请稍后重试');
    }
  });

  /**
   * 用户注册
   */
  register = this.asyncHandler(async (req, res) => {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.validationError(errors.array());
    }

    const { username, password, email, role = 'viewer' } = req.body;

    try {
      // 检查用户名是否已存在
      const existingUser = await User.findByUsername(username);
      if (existingUser) {
        return res.conflict('用户名已存在');
      }

      // 检查邮箱是否已存在
      if (email) {
        const existingEmail = await User.findByEmail(email);
        if (existingEmail) {
          return res.conflict('邮箱已被使用');
        }
      }

      // 加密密码
      const hashedPassword = await bcrypt.hash(password, this.saltRounds);

      // 创建用户
      const userData = {
        username,
        password: hashedPassword,
        email,
        role,
        status: 'active',
        createdAt: new Date().toISOString()
      };

      const newUser = await User.create(userData);
      const userResponse = this.sanitizeUser(newUser);

      this.logOperation(req, 'USER_REGISTER', { userId: newUser.id, username });

      res.success(userResponse, '注册成功', 201);
    } catch (error) {
      logger.error('注册失败', {
        error: error.message,
        username,
        email,
        ip: req.ip
      });
      res.internalError('注册失败，请稍后重试');
    }
  });

  /**
   * 获取当前用户信息
   */
  getCurrentUser = this.asyncHandler(async (req, res) => {
    try {
      const user = await User.findById(req.user.id);
      if (!user) {
        return res.notFound('用户不存在');
      }

      const userResponse = this.sanitizeUser(user);
      res.success(userResponse);
    } catch (error) {
      logger.error('获取用户信息失败', {
        error: error.message,
        userId: req.user?.id
      });
      res.internalError('获取用户信息失败');
    }
  });

  /**
   * 更新用户信息
   */
  updateProfile = this.asyncHandler(async (req, res) => {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.validationError(errors.array());
    }

    const { email, currentPassword, newPassword } = req.body;
    const userId = req.user.id;

    try {
      const user = await User.findById(userId);
      if (!user) {
        return res.notFound('用户不存在');
      }

      const updateData = {};

      // 更新邮箱
      if (email && email !== user.email) {
        const existingEmail = await User.findByEmail(email);
        if (existingEmail && existingEmail.id !== userId) {
          return res.conflict('邮箱已被使用');
        }
        updateData.email = email;
      }

      // 更新密码
      if (newPassword) {
        if (!currentPassword) {
          return res.badRequest('更新密码需要提供当前密码');
        }

        const isValidPassword = await bcrypt.compare(currentPassword, user.password);
        if (!isValidPassword) {
          return res.unauthorized('当前密码错误');
        }

        updateData.password = await bcrypt.hash(newPassword, this.saltRounds);
      }

      if (Object.keys(updateData).length === 0) {
        return res.badRequest('没有需要更新的数据');
      }

      updateData.updatedAt = new Date().toISOString();
      const updatedUser = await User.update(userId, updateData);
      const userResponse = this.sanitizeUser(updatedUser);

      this.logOperation(req, 'PROFILE_UPDATE', { userId, fields: Object.keys(updateData) });

      res.success(userResponse, '用户信息更新成功');
    } catch (error) {
      logger.error('更新用户信息失败', {
        error: error.message,
        userId
      });
      res.internalError('更新用户信息失败');
    }
  });

  /**
   * 用户登出
   */
  logout = this.asyncHandler(async (req, res) => {
    try {
      const token = req.headers.authorization?.replace('Bearer ', '');
      if (token) {
        // 将令牌加入黑名单
        await this.jwtManager.blacklistToken(token);
      }

      this.logOperation(req, 'LOGOUT', { userId: req.user?.id });

      res.success(null, '登出成功');
    } catch (error) {
      logger.error('登出失败', {
        error: error.message,
        userId: req.user?.id
      });
      res.internalError('登出失败');
    }
  });

  /**
   * 刷新访问令牌
   */
  refreshToken = this.asyncHandler(async (req, res) => {
    const { refreshToken } = req.body;

    if (!refreshToken) {
      return res.badRequest('缺少刷新令牌');
    }

    try {
      // 验证刷新令牌
      const decoded = this.jwtManager.verifyRefreshToken(refreshToken);

      // 检查用户是否仍然存在且活跃
      const user = await User.findById(decoded.id);
      if (!user || user.status !== 'active') {
        return res.unauthorized('用户不存在或已被禁用');
      }

      // 生成新的访问令牌
      const tokenPayload = {
        id: user.id,
        username: user.username,
        role: user.role,
        permissions: user.permissions || []
      };

      const newAccessToken = this.jwtManager.generateToken(tokenPayload);
      const newRefreshToken = this.jwtManager.generateRefreshToken(tokenPayload);

      // 将旧的刷新令牌加入黑名单
      await this.jwtManager.blacklistToken(refreshToken);

      res.success(
        {
          accessToken: newAccessToken,
          refreshToken: newRefreshToken,
          expiresIn: config.jwt.expiresIn
        },
        '令牌刷新成功'
      );
    } catch (error) {
      logger.error('刷新令牌失败', {
        error: error.message,
        ip: req.ip
      });

      if (error.name === 'JsonWebTokenError' || error.name === 'TokenExpiredError') {
        return res.unauthorized('无效的刷新令牌');
      }

      res.internalError('令牌刷新失败');
    }
  });

  /**
   * 清理用户敏感数据
   */
  sanitizeUser(user) {
    const { password, ...sanitizedUser } = user;
    return sanitizedUser;
  }

  /**
   * 验证用户权限
   */
  checkPermission = this.asyncHandler(async (req, res) => {
    const { permission } = req.params;
    const userPermissions = req.user.permissions || [];
    const hasPermission = userPermissions.includes(permission) || req.user.role === 'admin';

    res.success({ hasPermission }, '权限检查完成');
  });
}

export default new AuthController();

/**
 * 基础控制器类
 * 提供通用的控制器功能和错误处理
 */

import responseFormatter from '../middleware/responseFormatter.js';

class BaseController {
  constructor() {
    // 绑定方法到实例，确保正确的this上下文
    this.handleRequest = this.handleRequest.bind(this);
    this.handleError = this.handleError.bind(this);
    this.validateRequest = this.validateRequest.bind(this);
    this.getPaginationParams = this.getPaginationParams.bind(this);
    this.getSortingParams = this.getSortingParams.bind(this);
    this.getFilterParams = this.getFilterParams.bind(this);
  }

  /**
   * 通用请求处理包装器
   * @param {Function} handler - 处理函数
   * @returns {Function} Express路由处理函数
   */
  handleRequest(handler) {
    return async (req, res, next) => {
      try {
        await handler(req, res, next);
      } catch (error) {
        this.handleError(error, req, res, next);
      }
    };
  }

  /**
   * 统一错误处理
   * @param {Error} error - 错误对象
   * @param {Object} req - Express请求对象
   * @param {Object} res - Express响应对象
   * @param {Function} next - Express next函数
   */
  handleError(error, req, res, next) {
    console.error(`[${new Date().toISOString()}] Controller Error:`, {
      error: error.message,
      stack: error.stack,
      url: req.url,
      method: req.method,
      user: req.user?.username || 'anonymous',
      body: req.body,
      params: req.params,
      query: req.query
    });

    // 根据错误类型返回相应的响应
    if (error.name === 'ValidationError') {
      return responseFormatter.validationError(res, '数据验证失败', error.details);
    } else if (error.name === 'UnauthorizedError') {
      return responseFormatter.unauthorized(res, error.message);
    } else if (error.name === 'ForbiddenError') {
      return responseFormatter.forbidden(res, error.message);
    } else if (error.name === 'NotFoundError') {
      return responseFormatter.notFound(res, error.message);
    } else if (error.name === 'ConflictError') {
      return responseFormatter.conflict(res, error.message);
    } else if (error.name === 'TooManyRequestsError') {
      return responseFormatter.tooManyRequests(res, error.message);
    } 
    return responseFormatter.internalError(res, '服务器内部错误', error);

  }

  /**
   * 验证请求数据
   * @param {Object} req - Express请求对象
   * @param {Object} schema - 验证模式
   * @throws {ValidationError} 验证失败时抛出错误
   */
  validateRequest(req, schema) {
    const { error, value } = schema.validate(req.body, {
      abortEarly: false,
      stripUnknown: true
    });

    if (error) {
      const validationError = new Error('数据验证失败');
      validationError.name = 'ValidationError';
      validationError.details = error.details.map((detail) => ({
        field: detail.path.join('.'),
        message: detail.message,
        value: detail.context?.value
      }));
      throw validationError;
    }

    return value;
  }

  /**
   * 获取分页参数
   * @param {Object} req - Express请求对象
   * @returns {Object} 分页参数
   */
  getPaginationParams(req) {
    const page = parseInt(req.query.page) || 1;
    const limit = parseInt(req.query.limit) || 20;
    const offset = parseInt(req.query.offset) || (page - 1) * limit;

    return {
      page: Math.max(1, page),
      limit: Math.min(100, Math.max(1, limit)),
      offset: Math.max(0, offset)
    };
  }

  /**
   * 获取排序参数
   * @param {Object} req - Express请求对象
   * @param {Array} allowedFields - 允许排序的字段
   * @returns {Object} 排序参数
   */
  getSortingParams(req, allowedFields = []) {
    const sortBy = req.query.sortBy || 'createdAt';
    const sortOrder = (req.query.sortOrder || 'desc').toLowerCase();

    // 验证排序字段
    const validSortBy =
      allowedFields.length > 0 && !allowedFields.includes(sortBy) ? allowedFields[0] : sortBy;

    // 验证排序方向
    const validSortOrder = ['asc', 'desc'].includes(sortOrder) ? sortOrder : 'desc';

    return {
      sortBy: validSortBy,
      sortOrder: validSortOrder,
      orderBy: `${validSortBy} ${validSortOrder.toUpperCase()}`
    };
  }

  /**
   * 获取过滤参数
   * @param {Object} req - Express请求对象
   * @param {Array} allowedFilters - 允许的过滤字段
   * @returns {Object} 过滤参数
   */
  getFilterParams(req, allowedFilters = []) {
    const filters = {};

    // 处理查询参数中的过滤条件
    for (const [key, value] of Object.entries(req.query)) {
      if (allowedFilters.includes(key) && value !== undefined && value !== '') {
        // 处理不同类型的过滤条件
        if (key.endsWith('_min') || key.endsWith('_max')) {
          // 数值范围过滤
          const fieldName = key.replace(/_min$|_max$/, '');
          if (!filters[fieldName]) {filters[fieldName] = {};}

          if (key.endsWith('_min')) {
            filters[fieldName].min = parseFloat(value);
          } else {
            filters[fieldName].max = parseFloat(value);
          }
        } else if (key.endsWith('_like')) {
          // 模糊匹配
          const fieldName = key.replace(/_like$/, '');
          filters[fieldName] = { like: `%${value}%` };
        } else if (key.endsWith('_in')) {
          // 数组包含
          const fieldName = key.replace(/_in$/, '');
          filters[fieldName] = { in: Array.isArray(value) ? value : value.split(',') };
        } else {
          // 精确匹配
          filters[key] = value;
        }
      }
    }

    return filters;
  }

  /**
   * 获取搜索参数
   * @param {Object} req - Express请求对象
   * @returns {Object} 搜索参数
   */
  getSearchParams(req) {
    const keyword = req.query.keyword || req.query.q || '';
    const searchFields = req.query.searchFields
      ? req.query.searchFields.split(',').map((field) => field.trim())
      : [];

    return {
      keyword: keyword.trim(),
      searchFields,
      hasSearch: keyword.trim().length > 0
    };
  }

  /**
   * 获取日期范围参数
   * @param {Object} req - Express请求对象
   * @returns {Object} 日期范围参数
   */
  getDateRangeParams(req) {
    const startDate = req.query.startDate ? new Date(req.query.startDate) : null;
    const endDate = req.query.endDate ? new Date(req.query.endDate) : null;

    // 验证日期有效性
    const validStartDate = startDate && !isNaN(startDate.getTime()) ? startDate : null;
    const validEndDate = endDate && !isNaN(endDate.getTime()) ? endDate : null;

    // 确保结束日期不早于开始日期
    if (validStartDate && validEndDate && validEndDate < validStartDate) {
      const temp = validStartDate;
      validStartDate = validEndDate;
      validEndDate = temp;
    }

    return {
      startDate: validStartDate,
      endDate: validEndDate,
      hasDateRange: validStartDate || validEndDate
    };
  }

  /**
   * 构建查询条件
   * @param {Object} req - Express请求对象
   * @param {Object} options - 查询选项
   * @returns {Object} 查询条件
   */
  buildQueryConditions(req, options = {}) {
    const {
      allowedFilters = [],
      allowedSortFields = [],
      defaultSort = 'createdAt',
      searchFields = []
    } = options;

    const pagination = this.getPaginationParams(req);
    const sorting = this.getSortingParams(req, allowedSortFields);
    const filters = this.getFilterParams(req, allowedFilters);
    const search = this.getSearchParams(req);
    const dateRange = this.getDateRangeParams(req);

    return {
      pagination,
      sorting,
      filters,
      search,
      dateRange,
      // 构建完整的查询对象
      query: {
        ...filters,
        ...(search.hasSearch && searchFields.length > 0
          ? {
            $or: searchFields.map((field) => ({
              [field]: { $regex: search.keyword, $options: 'i' }
            }))
          }
          : {}),
        ...(dateRange.hasDateRange
          ? {
            ...(dateRange.startDate ? { createdAt: { $gte: dateRange.startDate } } : {}),
            ...(dateRange.endDate
              ? { createdAt: { ...filters.createdAt, $lte: dateRange.endDate } }
              : {})
          }
          : {})
      }
    };
  }

  /**
   * 格式化分页响应
   * @param {Array} data - 数据数组
   * @param {number} total - 总数
   * @param {Object} pagination - 分页参数
   * @returns {Object} 分页响应对象
   */
  formatPaginatedResponse(data, total, pagination) {
    const { page, limit } = pagination;
    const totalPages = Math.ceil(total / limit);
    const hasNextPage = page < totalPages;
    const hasPrevPage = page > 1;

    return {
      data,
      pagination: {
        current: page,
        total: totalPages,
        count: data.length,
        totalCount: total,
        limit,
        hasNext: hasNextPage,
        hasPrev: hasPrevPage,
        nextPage: hasNextPage ? page + 1 : null,
        prevPage: hasPrevPage ? page - 1 : null
      }
    };
  }

  /**
   * 检查资源是否存在
   * @param {*} resource - 资源对象
   * @param {string} resourceName - 资源名称
   * @throws {NotFoundError} 资源不存在时抛出错误
   */
  checkResourceExists(resource, resourceName = '资源') {
    if (!resource) {
      const error = new Error(`${resourceName}不存在`);
      error.name = 'NotFoundError';
      throw error;
    }
  }

  /**
   * 检查用户权限
   * @param {Object} req - Express请求对象
   * @param {string|Array} requiredRoles - 需要的角色
   * @throws {ForbiddenError} 权限不足时抛出错误
   */
  checkPermission(req, requiredRoles) {
    const userRole = req.user?.role;
    const roles = Array.isArray(requiredRoles) ? requiredRoles : [requiredRoles];

    if (!userRole || !roles.includes(userRole)) {
      const error = new Error('权限不足');
      error.name = 'ForbiddenError';
      throw error;
    }
  }

  /**
   * 检查资源所有权
   * @param {Object} req - Express请求对象
   * @param {Object} resource - 资源对象
   * @param {string} ownerField - 所有者字段名
   * @throws {ForbiddenError} 不是资源所有者时抛出错误
   */
  checkOwnership(req, resource, ownerField = 'userId') {
    const userId = req.user?.userId;
    const resourceOwnerId = resource[ownerField];

    if (req.user?.role !== 'admin' && userId !== resourceOwnerId) {
      const error = new Error('只能操作自己的资源');
      error.name = 'ForbiddenError';
      throw error;
    }
  }

  /**
   * 记录操作日志
   * @param {Object} req - Express请求对象
   * @param {string} action - 操作类型
   * @param {Object} details - 操作详情
   */
  logOperation(req, action, details = {}) {
    console.log(`[${new Date().toISOString()}] Operation Log:`, {
      action,
      user: req.user?.username || 'anonymous',
      userId: req.user?.userId,
      ip: req.ip,
      userAgent: req.get('User-Agent'),
      url: req.url,
      method: req.method,
      ...details
    });
  }

  /**
   * 清理敏感数据
   * @param {Object} data - 数据对象
   * @param {Array} sensitiveFields - 敏感字段列表
   * @returns {Object} 清理后的数据
   */
  sanitizeData(data, sensitiveFields = ['password', 'token', 'secret']) {
    if (!data || typeof data !== 'object') {
      return data;
    }

    const sanitized = Array.isArray(data) ? [] : {};

    for (const [key, value] of Object.entries(data)) {
      if (sensitiveFields.includes(key)) {
        sanitized[key] = '[REDACTED]';
      } else if (typeof value === 'object' && value !== null) {
        sanitized[key] = this.sanitizeData(value, sensitiveFields);
      } else {
        sanitized[key] = value;
      }
    }

    return sanitized;
  }

  /**
   * 成功响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 响应消息
   * @param {Object} data - 响应数据
   * @param {number} statusCode - HTTP状态码
   */
  success(res, message, data = null, statusCode = 200) {
    return responseFormatter.success(res, message, data, statusCode);
  }

  /**
   * 错误响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 错误消息
   * @param {number} statusCode - HTTP状态码
   * @param {Object} details - 错误详情
   */
  error(res, message, statusCode = 500, details = null) {
    return responseFormatter.error(res, message, statusCode, details);
  }

  /**
   * 验证错误响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 错误消息
   * @param {Object} details - 验证错误详情
   */
  validationError(res, message, details = null) {
    return responseFormatter.validationError(res, message, details);
  }

  /**
   * 未授权响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 错误消息
   */
  unauthorized(res, message = '未授权访问') {
    return responseFormatter.unauthorized(res, message);
  }

  /**
   * 禁止访问响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 错误消息
   */
  forbidden(res, message = '禁止访问') {
    return responseFormatter.forbidden(res, message);
  }

  /**
   * 资源未找到响应
   * @param {Object} res - Express响应对象
   * @param {string} message - 错误消息
   */
  notFound(res, message = '资源未找到') {
    return responseFormatter.notFound(res, message);
  }
}

export default BaseController;

import { validationResult } from 'express-validator';
import BaseController from './BaseController.js';
import CarbonEmission from '../../../core/entities/CarbonEmission.js';
import CarbonFactor from '../../../core/entities/CarbonFactor.js';
import EnergyData from '../../../core/entities/EnergyData.js';
import logger from '../../../shared/utils/logger.js';
import { calculateTotalEmissions } from '../../../core/services/emission.js';

/**
 * 碳排放控制器
 * 处理碳排放相关的业务逻辑
 */
class CarbonController extends BaseController {
  constructor() {
    super();
  }

  /**
   * 获取碳排放数据列表
   */
  getCarbonEmissions = this.asyncHandler(async (req, res) => {
    try {
      const pagination = this.getPaginationParams(req);
      const sorting = this.getSortingParams(req, 'timestamp', 'desc');
      const filters = this.getFilterParams(req, ['device_id', 'emission_source']);
      const dateRange = this.getDateRangeParams(req);

      const queryConditions = {
        ...filters,
        ...dateRange
      };

      const result = await CarbonEmission.findWithPagination({
        conditions: queryConditions,
        pagination,
        sorting
      });

      const response = this.formatPaginatedResponse(result, pagination);
      res.success(response);
    } catch (error) {
      logger.error('获取碳排放数据列表失败', {
        error: error.message,
        userId: req.user?.id
      });
      res.internalError('获取碳排放数据列表失败');
    }
  });

  /**
   * 获取单个碳排放记录
   */
  getCarbonEmission = this.asyncHandler(async (req, res) => {
    const { id } = req.params;

    try {
      const carbonRecord = await CarbonEmission.findById(id);
      if (!carbonRecord) {
        return res.notFound('碳排放记录不存在');
      }

      res.success(carbonRecord);
    } catch (error) {
      logger.error('获取碳排放记录失败', {
        error: error.message,
        recordId: id,
        userId: req.user?.id
      });
      res.internalError('获取碳排放记录失败');
    }
  });

  /**
   * 创建碳排放数据记录
   */
  createCarbonEmission = this.asyncHandler(async (req, res) => {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.validationError(errors.array());
    }

    try {
      const carbonData = {
        ...req.body,
        created_by: req.user.id,
        created_at: new Date().toISOString()
      };

      // 设置默认时间戳
      if (!carbonData.timestamp) {
        carbonData.timestamp = new Date().toISOString();
      }

      const newRecord = await CarbonEmission.create(carbonData);

      this.logOperation(req, 'CARBON_EMISSION_CREATE', {
        recordId: newRecord.id,
        deviceId: carbonData.device_id,
        emission: carbonData.emission_amount
      });

      res.success(newRecord, '碳排放数据创建成功', 201);
    } catch (error) {
      logger.error('创建碳排放数据失败', {
        error: error.message,
        carbonData: req.body,
        userId: req.user?.id
      });
      res.internalError('创建碳排放数据失败');
    }
  });

  /**
   * 基于能耗数据计算碳排放
   */
  calculateCarbonFromEnergy = this.asyncHandler(async (req, res) => {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.validationError(errors.array());
    }

    try {
      const { energy_data_ids, carbon_factor_id } = req.body;

      // 验证碳排放因子
      const carbonFactor = await CarbonFactor.findById(carbon_factor_id);
      if (!carbonFactor) {
        return res.badRequest('碳排放因子不存在');
      }

      // 获取能耗数据
      const energyRecords = await EnergyData.findByIds(energy_data_ids);
      if (energyRecords.length === 0) {
        return res.badRequest('未找到有效的能耗数据');
      }

      // 计算碳排放
      const carbonEmissions = [];
      for (const energyRecord of energyRecords) {
        const emissionAmount = energyRecord.consumption * carbonFactor.factor_value;

        const carbonData = {
          device_id: energyRecord.device_id,
          emission_source: energyRecord.energy_type,
          emission_amount: emissionAmount,
          emission_unit: carbonFactor.unit,
          energy_consumption: energyRecord.consumption,
          carbon_factor_id,
          calculation_method: 'energy_based',
          timestamp: energyRecord.timestamp,
          created_by: req.user.id,
          created_at: new Date().toISOString()
        };

        const newEmission = await CarbonEmission.create(carbonData);
        carbonEmissions.push(newEmission);
      }

      this.logOperation(req, 'CARBON_CALCULATION', {
        energyRecordCount: energyRecords.length,
        carbonFactorId: carbon_factor_id,
        totalEmission: carbonEmissions.reduce((sum, e) => sum + e.emission_amount, 0)
      });

      res.success(
        {
          calculated_emissions: carbonEmissions,
          total_emission: carbonEmissions.reduce((sum, e) => sum + e.emission_amount, 0),
          carbon_factor: carbonFactor
        },
        '碳排放计算成功',
        201
      );
    } catch (error) {
      logger.error('碳排放计算失败', {
        error: error.message,
        requestData: req.body,
        userId: req.user?.id
      });
      res.internalError('碳排放计算失败');
    }
  });

  /**
   * 获取碳排放统计数据
   */
  getCarbonStatistics = this.asyncHandler(async (req, res) => {
    try {
      const dateRange = this.getDateRangeParams(req);
      const { device_id, emission_source, group_by = 'day' } = req.query;

      const filters = {
        ...dateRange
      };

      if (device_id) {filters.device_id = device_id;}
      if (emission_source) {filters.emission_source = emission_source;}

      const statistics = await CarbonEmission.getStatistics({
        filters,
        groupBy: group_by
      });

      res.success(statistics);
    } catch (error) {
      logger.error('获取碳排放统计数据失败', {
        error: error.message,
        filters: req.query,
        userId: req.user?.id
      });
      res.internalError('获取碳排放统计数据失败');
    }
  });

  /**
   * 获取碳排放趋势数据
   */
  getCarbonTrend = this.asyncHandler(async (req, res) => {
    try {
      const { start_time, end_time, interval = 'day' } = req.query;

      if (!start_time || !end_time) {
        return res.badRequest('缺少必要参数: start_time, end_time');
      }

      // 使用现有的碳排放计算服务
      const trendData = calculateTotalEmissions(start_time, end_time, interval);

      this.logOperation(req, 'CARBON_TREND_QUERY', {
        timeRange: { start_time, end_time },
        interval
      });

      res.success({
        trend: trendData,
        unit: 'kgCO2',
        time_range: {
          start: start_time,
          end: end_time
        },
        interval
      });
    } catch (error) {
      logger.error('获取碳排放趋势数据失败', {
        error: error.message,
        timeRange: { start_time: req.query.start_time, end_time: req.query.end_time },
        userId: req.user?.id
      });
      res.internalError('获取碳排放趋势数据失败');
    }
  });

  /**
   * 获取碳排放因子列表
   */
  getCarbonFactors = this.asyncHandler(async (req, res) => {
    try {
      const pagination = this.getPaginationParams(req);
      const filters = this.getFilterParams(req, ['energy_type', 'region']);

      const result = await CarbonFactor.findWithPagination({
        conditions: filters,
        pagination
      });

      const response = this.formatPaginatedResponse(result, pagination);
      res.success(response);
    } catch (error) {
      logger.error('获取碳排放因子列表失败', {
        error: error.message,
        userId: req.user?.id
      });
      res.internalError('获取碳排放因子列表失败');
    }
  });

  /**
   * 创建碳排放因子
   */
  createCarbonFactor = this.asyncHandler(async (req, res) => {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.validationError(errors.array());
    }

    try {
      const factorData = {
        ...req.body,
        created_by: req.user.id,
        created_at: new Date().toISOString()
      };

      const newFactor = await CarbonFactor.create(factorData);

      this.logOperation(req, 'CARBON_FACTOR_CREATE', {
        factorId: newFactor.id,
        energyType: factorData.energy_type,
        factorValue: factorData.factor_value
      });

      res.success(newFactor, '碳排放因子创建成功', 201);
    } catch (error) {
      logger.error('创建碳排放因子失败', {
        error: error.message,
        factorData: req.body,
        userId: req.user?.id
      });
      res.internalError('创建碳排放因子失败');
    }
  });

  /**
   * 更新碳排放因子
   */
  updateCarbonFactor = this.asyncHandler(async (req, res) => {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.validationError(errors.array());
    }

    const { id } = req.params;

    try {
      const factor = await CarbonFactor.findById(id);
      if (!factor) {
        return res.notFound('碳排放因子不存在');
      }

      const updateData = {
        ...req.body,
        updated_by: req.user.id,
        updated_at: new Date().toISOString()
      };

      const updatedFactor = await CarbonFactor.update(id, updateData);

      this.logOperation(req, 'CARBON_FACTOR_UPDATE', {
        factorId: id,
        changes: Object.keys(updateData)
      });

      res.success(updatedFactor, '碳排放因子更新成功');
    } catch (error) {
      logger.error('更新碳排放因子失败', {
        error: error.message,
        factorId: id,
        updateData: req.body,
        userId: req.user?.id
      });
      res.internalError('更新碳排放因子失败');
    }
  });

  /**
   * 生成碳排放报告
   */
  generateCarbonReport = this.asyncHandler(async (req, res) => {
    try {
      const dateRange = this.getDateRangeParams(req);
      const { report_type = 'summary', device_ids } = req.query;

      const filters = {
        ...dateRange
      };

      if (device_ids) {
        filters.device_id = device_ids.split(',');
      }

      const report = await CarbonEmission.generateReport({
        filters,
        reportType: report_type
      });

      this.logOperation(req, 'CARBON_REPORT_GENERATE', {
        reportType: report_type,
        timeRange: dateRange,
        deviceCount: device_ids ? device_ids.split(',').length : 'all'
      });

      res.success({
        report,
        generated_at: new Date().toISOString(),
        report_type,
        time_range: dateRange
      });
    } catch (error) {
      logger.error('生成碳排放报告失败', {
        error: error.message,
        reportType: req.query.report_type,
        userId: req.user?.id
      });
      res.internalError('生成碳排放报告失败');
    }
  });

  /**
   * 导出碳排放数据
   */
  exportCarbonData = this.asyncHandler(async (req, res) => {
    try {
      const dateRange = this.getDateRangeParams(req);
      const filters = this.getFilterParams(req, ['device_id', 'emission_source']);
      const { format = 'csv' } = req.query;

      const queryConditions = {
        ...filters,
        ...dateRange
      };

      let exportData;
      let contentType;
      let fileExtension;

      switch (format.toLowerCase()) {
        case 'csv':
          exportData = await CarbonEmission.exportToCSV(queryConditions);
          contentType = 'text/csv';
          fileExtension = 'csv';
          break;
        case 'json':
          exportData = await CarbonEmission.exportToJSON(queryConditions);
          contentType = 'application/json';
          fileExtension = 'json';
          break;
        default:
          return res.badRequest('不支持的导出格式');
      }

      this.logOperation(req, 'CARBON_DATA_EXPORT', {
        format,
        filters: queryConditions,
        recordCount: exportData.recordCount
      });

      res.downloadFile(
        exportData.content,
        `carbon_emissions_${Date.now()}.${fileExtension}`,
        contentType
      );
    } catch (error) {
      logger.error('导出碳排放数据失败', {
        error: error.message,
        format: req.query.format,
        filters: req.query,
        userId: req.user?.id
      });
      res.internalError('导出碳排放数据失败');
    }
  });

  /**
   * 删除碳排放数据记录
   */
  deleteCarbonEmission = this.asyncHandler(async (req, res) => {
    const { id } = req.params;

    try {
      const carbonRecord = await CarbonEmission.findById(id);
      if (!carbonRecord) {
        return res.notFound('碳排放记录不存在');
      }

      await CarbonEmission.delete(id);

      this.logOperation(req, 'CARBON_EMISSION_DELETE', {
        recordId: id,
        deviceId: carbonRecord.device_id
      });

      res.success(null, '碳排放数据删除成功');
    } catch (error) {
      logger.error('删除碳排放数据失败', {
        error: error.message,
        recordId: id,
        userId: req.user?.id
      });
      res.internalError('删除碳排放数据失败');
    }
  });

  /**
   * 获取碳排放预测
   */
  getCarbonPrediction = this.asyncHandler(async (req, res) => {
    try {
      const { device_id, prediction_days = 7 } = req.query;

      const filters = {};
      if (device_id) {filters.device_id = device_id;}

      const prediction = await CarbonEmission.getPrediction({
        filters,
        predictionDays: parseInt(prediction_days)
      });

      res.success({
        prediction_days: parseInt(prediction_days),
        prediction_data: prediction,
        generated_at: new Date().toISOString()
      });
    } catch (error) {
      logger.error('获取碳排放预测失败', {
        error: error.message,
        deviceId: req.query.device_id,
        userId: req.user?.id
      });
      res.internalError('获取碳排放预测失败');
    }
  });
}

export default new CarbonController();

import { validationResult } from 'express-validator';
import BaseController from './BaseController.js';
import Device from '../../../core/entities/Device.js';
import DeviceType from '../../../core/entities/DeviceType.js';
import logger from '../../../shared/utils/logger.js';

/**
 * 设备管理控制器
 * 处理设备相关的业务逻辑
 */
class DeviceController extends BaseController {
  constructor() {
    super();
  }

  /**
   * 获取设备列表
   */
  getDevices = this.asyncHandler(async (req, res) => {
    try {
      const pagination = this.getPaginationParams(req);
      const sorting = this.getSortingParams(req, 'created_at', 'desc');
      const filters = this.getFilterParams(req, ['type', 'status', 'location']);
      const search = this.getSearchParams(req, ['name', 'serial_number', 'model']);
      const dateRange = this.getDateRangeParams(req);

      // 构建查询条件
      const queryConditions = {
        ...filters,
        ...search,
        ...dateRange
      };

      const result = await Device.findWithPagination({
        conditions: queryConditions,
        pagination,
        sorting
      });

      const response = this.formatPaginatedResponse(result, pagination);
      res.success(response);
    } catch (error) {
      logger.error('获取设备列表失败', {
        error: error.message,
        userId: req.user?.id
      });
      res.internalError('获取设备列表失败');
    }
  });

  /**
   * 获取单个设备详情
   */
  getDevice = this.asyncHandler(async (req, res) => {
    const { id } = req.params;

    try {
      const device = await Device.findById(id);
      if (!device) {
        return res.notFound('设备不存在');
      }

      // 获取设备类型信息
      if (device.type_id) {
        device.type_info = await DeviceType.findById(device.type_id);
      }

      res.success(device);
    } catch (error) {
      logger.error('获取设备详情失败', {
        error: error.message,
        deviceId: id,
        userId: req.user?.id
      });
      res.internalError('获取设备详情失败');
    }
  });

  /**
   * 创建新设备
   */
  createDevice = this.asyncHandler(async (req, res) => {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.validationError(errors.array());
    }

    try {
      const deviceData = {
        ...req.body,
        created_by: req.user.id,
        created_at: new Date().toISOString(),
        status: req.body.status || 'offline'
      };

      // 检查序列号是否已存在
      if (deviceData.serial_number) {
        const existingDevice = await Device.findBySerialNumber(deviceData.serial_number);
        if (existingDevice) {
          return res.conflict('设备序列号已存在');
        }
      }

      // 验证设备类型
      if (deviceData.type_id) {
        const deviceType = await DeviceType.findById(deviceData.type_id);
        if (!deviceType) {
          return res.badRequest('无效的设备类型');
        }
      }

      const newDevice = await Device.create(deviceData);

      this.logOperation(req, 'DEVICE_CREATE', {
        deviceId: newDevice.id,
        deviceName: newDevice.name
      });

      res.success(newDevice, '设备创建成功', 201);
    } catch (error) {
      logger.error('创建设备失败', {
        error: error.message,
        deviceData: req.body,
        userId: req.user?.id
      });
      res.internalError('创建设备失败');
    }
  });

  /**
   * 更新设备信息
   */
  updateDevice = this.asyncHandler(async (req, res) => {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.validationError(errors.array());
    }

    const { id } = req.params;

    try {
      const device = await Device.findById(id);
      if (!device) {
        return res.notFound('设备不存在');
      }

      const updateData = {
        ...req.body,
        updated_by: req.user.id,
        updated_at: new Date().toISOString()
      };

      // 检查序列号冲突（如果更新了序列号）
      if (updateData.serial_number && updateData.serial_number !== device.serial_number) {
        const existingDevice = await Device.findBySerialNumber(updateData.serial_number);
        if (existingDevice && existingDevice.id !== id) {
          return res.conflict('设备序列号已存在');
        }
      }

      // 验证设备类型（如果更新了类型）
      if (updateData.type_id && updateData.type_id !== device.type_id) {
        const deviceType = await DeviceType.findById(updateData.type_id);
        if (!deviceType) {
          return res.badRequest('无效的设备类型');
        }
      }

      const updatedDevice = await Device.update(id, updateData);

      this.logOperation(req, 'DEVICE_UPDATE', {
        deviceId: id,
        deviceName: updatedDevice.name,
        changes: Object.keys(updateData)
      });

      res.success(updatedDevice, '设备更新成功');
    } catch (error) {
      logger.error('更新设备失败', {
        error: error.message,
        deviceId: id,
        updateData: req.body,
        userId: req.user?.id
      });
      res.internalError('更新设备失败');
    }
  });

  /**
   * 删除设备
   */
  deleteDevice = this.asyncHandler(async (req, res) => {
    const { id } = req.params;

    try {
      const device = await Device.findById(id);
      if (!device) {
        return res.notFound('设备不存在');
      }

      // 检查设备是否有关联数据
      const hasRelatedData = await Device.hasRelatedData(id);
      if (hasRelatedData) {
        return res.conflict('设备存在关联数据，无法删除');
      }

      await Device.delete(id);

      this.logOperation(req, 'DEVICE_DELETE', {
        deviceId: id,
        deviceName: device.name
      });

      res.success(null, '设备删除成功');
    } catch (error) {
      logger.error('删除设备失败', {
        error: error.message,
        deviceId: id,
        userId: req.user?.id
      });
      res.internalError('删除设备失败');
    }
  });

  /**
   * 获取设备类型列表
   */
  getDeviceTypes = this.asyncHandler(async (req, res) => {
    try {
      const deviceTypes = await DeviceType.findAll();
      res.success(deviceTypes);
    } catch (error) {
      logger.error('获取设备类型列表失败', {
        error: error.message,
        userId: req.user?.id
      });
      res.internalError('获取设备类型列表失败');
    }
  });

  /**
   * 获取设备状态统计
   */
  getDeviceStatusStats = this.asyncHandler(async (req, res) => {
    try {
      const stats = await Device.getStatusStatistics();
      res.success(stats);
    } catch (error) {
      logger.error('获取设备状态统计失败', {
        error: error.message,
        userId: req.user?.id
      });
      res.internalError('获取设备状态统计失败');
    }
  });

  /**
   * 获取设备类型统计
   */
  getDeviceTypeStats = this.asyncHandler(async (req, res) => {
    try {
      const stats = await Device.getTypeStatistics();
      res.success(stats);
    } catch (error) {
      logger.error('获取设备类型统计失败', {
        error: error.message,
        userId: req.user?.id
      });
      res.internalError('获取设备类型统计失败');
    }
  });

  /**
   * 批量更新设备状态
   */
  batchUpdateStatus = this.asyncHandler(async (req, res) => {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.validationError(errors.array());
    }

    const { device_ids, status } = req.body;

    try {
      const updateData = {
        status,
        updated_by: req.user.id,
        updated_at: new Date().toISOString()
      };

      const result = await Device.batchUpdate(device_ids, updateData);

      this.logOperation(req, 'DEVICE_BATCH_UPDATE', {
        deviceIds: device_ids,
        status,
        affectedCount: result.affectedRows
      });

      res.success(
        {
          affected_count: result.affectedRows,
          status
        },
        '批量更新设备状态成功'
      );
    } catch (error) {
      logger.error('批量更新设备状态失败', {
        error: error.message,
        deviceIds: req.body.device_ids,
        status: req.body.status,
        userId: req.user?.id
      });
      res.internalError('批量更新设备状态失败');
    }
  });

  /**
   * 设备健康检查
   */
  healthCheck = this.asyncHandler(async (req, res) => {
    const { id } = req.params;

    try {
      const device = await Device.findById(id);
      if (!device) {
        return res.notFound('设备不存在');
      }

      // 执行设备健康检查
      const healthStatus = await Device.performHealthCheck(id);

      // 更新设备健康状态
      await Device.update(id, {
        health_status: healthStatus.status,
        last_health_check: new Date().toISOString(),
        updated_by: req.user.id,
        updated_at: new Date().toISOString()
      });

      this.logOperation(req, 'DEVICE_HEALTH_CHECK', {
        deviceId: id,
        healthStatus: healthStatus.status
      });

      res.success(healthStatus, '设备健康检查完成');
    } catch (error) {
      logger.error('设备健康检查失败', {
        error: error.message,
        deviceId: id,
        userId: req.user?.id
      });
      res.internalError('设备健康检查失败');
    }
  });

  /**
   * 获取设备历史数据
   */
  getDeviceHistory = this.asyncHandler(async (req, res) => {
    const { id } = req.params;
    const dateRange = this.getDateRangeParams(req);
    const pagination = this.getPaginationParams(req);

    try {
      const device = await Device.findById(id);
      if (!device) {
        return res.notFound('设备不存在');
      }

      const history = await Device.getHistory(id, {
        ...dateRange,
        ...pagination
      });

      const response = this.formatPaginatedResponse(history, pagination);
      res.success(response);
    } catch (error) {
      logger.error('获取设备历史数据失败', {
        error: error.message,
        deviceId: id,
        userId: req.user?.id
      });
      res.internalError('获取设备历史数据失败');
    }
  });
}

export default new DeviceController();

import BaseController from './BaseController.js';
import DigitalTwin from '../../../domain/entities/DigitalTwin.js';
import Device from '../../../domain/entities/Device.js';
import logger from '../../../utils/logger.js';

class DigitalTwinController extends BaseController {
  /**
   * 获取数字孪生列表
   */
  async getDigitalTwins(req, res) {
    try {
      const { page = 1, limit = 20, deviceId, twinType, status, buildingId } = req.query;

      const filters = {};
      if (deviceId) {filters.deviceId = deviceId;}
      if (twinType) {filters.twinType = twinType;}
      if (status) {filters.status = status;}
      if (buildingId) {filters.buildingId = buildingId;}

      const offset = (page - 1) * limit;
      const digitalTwins = await DigitalTwin.findWithPagination(filters, offset, parseInt(limit));
      const total = await DigitalTwin.countDocuments(filters);

      return this.success(res, '获取数字孪生列表成功', {
        data: digitalTwins,
        pagination: {
          page: parseInt(page),
          limit: parseInt(limit),
          total,
          pages: Math.ceil(total / limit)
        }
      });
    } catch (error) {
      logger.error('获取数字孪生列表错误:', error);
      return this.error(res, '获取数字孪生列表失败', 500);
    }
  }

  /**
   * 获取单个数字孪生
   */
  async getDigitalTwin(req, res) {
    try {
      const digitalTwin = await DigitalTwin.findByIdWithDetails(req.params.id);
      if (!digitalTwin) {
        return this.error(res, '数字孪生不存在', 404);
      }

      return this.success(res, '获取数字孪生成功', { digitalTwin });
    } catch (error) {
      logger.error('获取数字孪生错误:', error);
      return this.error(res, '获取数字孪生失败', 500);
    }
  }

  /**
   * 创建数字孪生
   */
  async createDigitalTwin(req, res) {
    try {
      // 验证关联设备是否存在
      if (req.body.deviceId) {
        const device = await Device.findById(req.body.deviceId);
        if (!device) {
          return this.error(res, '关联设备不存在', 404);
        }
      }

      const digitalTwinData = {
        ...req.body,
        createdBy: req.user.userId,
        createdAt: new Date()
      };

      const newDigitalTwin = await DigitalTwin.create(digitalTwinData);

      return this.success(res, '数字孪生创建成功', { digitalTwin: newDigitalTwin }, 201);
    } catch (error) {
      logger.error('创建数字孪生错误:', error);
      return this.error(res, '创建数字孪生失败', 500);
    }
  }

  /**
   * 更新数字孪生
   */
  async updateDigitalTwin(req, res) {
    try {
      const digitalTwin = await DigitalTwin.findById(req.params.id);
      if (!digitalTwin) {
        return this.error(res, '数字孪生不存在', 404);
      }

      const updateData = {
        ...req.body,
        updatedBy: req.user.userId,
        updatedAt: new Date()
      };

      const updatedDigitalTwin = await digitalTwin.update(updateData);

      return this.success(res, '数字孪生更新成功', { digitalTwin: updatedDigitalTwin });
    } catch (error) {
      logger.error('更新数字孪生错误:', error);
      return this.error(res, '更新数字孪生失败', 500);
    }
  }

  /**
   * 删除数字孪生
   */
  async deleteDigitalTwin(req, res) {
    try {
      const digitalTwin = await DigitalTwin.findById(req.params.id);
      if (!digitalTwin) {
        return this.error(res, '数字孪生不存在', 404);
      }

      await digitalTwin.delete();

      return this.success(res, '数字孪生删除成功');
    } catch (error) {
      logger.error('删除数字孪生错误:', error);
      return this.error(res, '删除数字孪生失败', 500);
    }
  }

  /**
   * 获取数字孪生实时数据
   */
  async getDigitalTwinRealTimeData(req, res) {
    try {
      const digitalTwin = await DigitalTwin.findById(req.params.id);
      if (!digitalTwin) {
        return this.error(res, '数字孪生不存在', 404);
      }

      const realTimeData = await digitalTwin.getRealTimeData();

      return this.success(res, '获取实时数据成功', { realTimeData });
    } catch (error) {
      logger.error('获取数字孪生实时数据错误:', error);
      return this.error(res, '获取实时数据失败', 500);
    }
  }

  /**
   * 更新数字孪生状态
   */
  async updateDigitalTwinStatus(req, res) {
    try {
      const digitalTwin = await DigitalTwin.findById(req.params.id);
      if (!digitalTwin) {
        return this.error(res, '数字孪生不存在', 404);
      }

      const { status, statusData } = req.body;
      const updatedDigitalTwin = await digitalTwin.updateStatus(
        status,
        statusData,
        req.user.userId
      );

      return this.success(res, '数字孪生状态更新成功', { digitalTwin: updatedDigitalTwin });
    } catch (error) {
      logger.error('更新数字孪生状态错误:', error);
      return this.error(res, '更新状态失败', 500);
    }
  }

  /**
   * 获取数字孪生模拟数据
   */
  async getDigitalTwinSimulationData(req, res) {
    try {
      const digitalTwin = await DigitalTwin.findById(req.params.id);
      if (!digitalTwin) {
        return this.error(res, '数字孪生不存在', 404);
      }

      const { startDate, endDate, simulationType, parameters } = req.query;

      const simulationData = await digitalTwin.getSimulationData({
        startDate: startDate ? new Date(startDate) : null,
        endDate: endDate ? new Date(endDate) : null,
        simulationType,
        parameters: parameters ? JSON.parse(parameters) : {}
      });

      return this.success(res, '获取模拟数据成功', { simulationData });
    } catch (error) {
      logger.error('获取数字孪生模拟数据错误:', error);
      return this.error(res, '获取模拟数据失败', 500);
    }
  }

  /**
   * 运行数字孪生模拟
   */
  async runDigitalTwinSimulation(req, res) {
    try {
      const digitalTwin = await DigitalTwin.findById(req.params.id);
      if (!digitalTwin) {
        return this.error(res, '数字孪生不存在', 404);
      }

      const { simulationType, parameters, duration } = req.body;

      const simulationResult = await digitalTwin.runSimulation({
        simulationType,
        parameters,
        duration,
        initiatedBy: req.user.userId
      });

      return this.success(res, '模拟运行成功', { simulationResult });
    } catch (error) {
      logger.error('运行数字孪生模拟错误:', error);
      return this.error(res, '运行模拟失败', 500);
    }
  }

  /**
   * 获取数字孪生统计数据
   */
  async getDigitalTwinStats(req, res) {
    try {
      const { twinType, buildingId, status } = req.query;

      const filters = {};
      if (twinType) {filters.twinType = twinType;}
      if (buildingId) {filters.buildingId = buildingId;}
      if (status) {filters.status = status;}

      const stats = await DigitalTwin.getStatistics(filters);

      return this.success(res, '获取数字孪生统计成功', { stats });
    } catch (error) {
      logger.error('获取数字孪生统计错误:', error);
      return this.error(res, '获取统计数据失败', 500);
    }
  }

  /**
   * 同步数字孪生数据
   */
  async syncDigitalTwinData(req, res) {
    try {
      const digitalTwin = await DigitalTwin.findById(req.params.id);
      if (!digitalTwin) {
        return this.error(res, '数字孪生不存在', 404);
      }

      const syncResult = await digitalTwin.syncWithRealWorld(req.user.userId);

      return this.success(res, '数据同步成功', { syncResult });
    } catch (error) {
      logger.error('同步数字孪生数据错误:', error);
      return this.error(res, '数据同步失败', 500);
    }
  }

  /**
   * 获取数字孪生性能指标
   */
  async getDigitalTwinPerformance(req, res) {
    try {
      const digitalTwin = await DigitalTwin.findById(req.params.id);
      if (!digitalTwin) {
        return this.error(res, '数字孪生不存在', 404);
      }

      const { startDate, endDate, metrics } = req.query;

      const performance = await digitalTwin.getPerformanceMetrics({
        startDate: startDate ? new Date(startDate) : null,
        endDate: endDate ? new Date(endDate) : null,
        metrics: metrics ? metrics.split(',') : []
      });

      return this.success(res, '获取性能指标成功', { performance });
    } catch (error) {
      logger.error('获取数字孪生性能指标错误:', error);
      return this.error(res, '获取性能指标失败', 500);
    }
  }

  /**
   * 导出数字孪生数据
   */
  async exportDigitalTwinData(req, res) {
    try {
      const { twinType, buildingId, status, format = 'csv' } = req.query;

      const filters = {};
      if (twinType) {filters.twinType = twinType;}
      if (buildingId) {filters.buildingId = buildingId;}
      if (status) {filters.status = status;}

      let exportData;
      let contentType;
      let filename;

      if (format === 'json') {
        exportData = await DigitalTwin.exportToJSON(filters);
        contentType = 'application/json';
        filename = 'digital-twins.json';
      } else {
        exportData = await DigitalTwin.exportToCSV(filters);
        contentType = 'text/csv';
        filename = 'digital-twins.csv';
      }

      res.setHeader('Content-Type', contentType);
      res.setHeader('Content-Disposition', `attachment; filename=${filename}`);
      res.send(exportData);
    } catch (error) {
      logger.error('导出数字孪生数据错误:', error);
      return this.error(res, '导出数据失败', 500);
    }
  }
}

