  admin: [
    'user:create',
    'user:read',
    'user:update',
    'user:delete',
    'device:create',
    'device:read',
    'device:update',
    'device:delete',
    'energy:create',
    'energy:read',
    'energy:update',
    'energy:delete',
    'carbon:create',
    'carbon:read',
    'carbon:update',
    'carbon:delete',
    'maintenance:create',
    'maintenance:read',
    'maintenance:update',
    'maintenance:delete',
    'digitaltwin:create',
    'digitaltwin:read',
    'digitaltwin:update',
    'digitaltwin:delete',
    'system:config',
    'system:backup',
    'system:restore',
    'report:generate',
    'report:export'
  ],
  operator: [
    'device:create',
    'device:read',
    'device:update',
    'energy:create',
    'energy:read',
    'energy:update',
    'carbon:create',
    'carbon:read',
    'carbon:update',
    'maintenance:create',
    'maintenance:read',
    'maintenance:update',
    'digitaltwin:create',
    'digitaltwin:read',
    'digitaltwin:update',
    'report:generate',
    'report:export'
  ],
  user: [
    'device:read',
    'energy:read',
    'carbon:read',
    'maintenance:read',
    'digitaltwin:read',
    'report:generate'
  ],
  guest: ['device:read', 'energy:read', 'carbon:read']
};

/**
 * 检查用户是否具有指定角色
 * @param {Array|string} allowedRoles - 允许的角色列表
 * @returns {Function} Express中间件函数
 */
function requireRole(allowedRoles) {
  // 确保allowedRoles是数组
  const roles = Array.isArray(allowedRoles) ? allowedRoles : [allowedRoles];

  return (req, res, next) => {
    try {
      // 检查用户是否已认证
      if (!req.user) {
        return responseFormatter.unauthorized(res, '用户未认证');
      }

      const userRole = req.user.role;

      // 检查用户角色是否在允许的角色列表中
      if (!roles.includes(userRole)) {
        return responseFormatter.forbidden(res, `需要以下角色之一: ${roles.join(', ')}`);
      }

      // 记录权限检查日志
      console.log(
        `[${new Date().toISOString()}] Role Check: User ${req.user.username} (${userRole}) accessing ${req.method} ${req.path}`
      );

      next();
    } catch (error) {
      console.error('角色检查错误:', error);
      return responseFormatter.internalError(res, '权限验证失败', error);
    }
  };
}

/**
 * 检查用户是否具有指定权限
 * @param {Array|string} requiredPermissions - 需要的权限列表
 * @returns {Function} Express中间件函数
 */
function requirePermission(requiredPermissions) {
  // 确保requiredPermissions是数组
  const permissions = Array.isArray(requiredPermissions)
    ? requiredPermissions
    : [requiredPermissions];

  return (req, res, next) => {
    try {
      // 检查用户是否已认证
      if (!req.user) {
        return responseFormatter.unauthorized(res, '用户未认证');
      }

      const userRole = req.user.role;
      const userPermissions = ROLE_PERMISSIONS[userRole] || [];

      // 检查用户是否具有所有需要的权限
      const hasAllPermissions = permissions.every((permission) =>
        userPermissions.includes(permission)
      );

      if (!hasAllPermissions) {
        const missingPermissions = permissions.filter(
          (permission) => !userPermissions.includes(permission)
        );

        return responseFormatter.forbidden(res, `缺少权限: ${missingPermissions.join(', ')}`);
      }

      // 记录权限检查日志
      console.log(
        `[${new Date().toISOString()}] Permission Check: User ${req.user.username} (${userRole}) has permissions: ${permissions.join(', ')}`
      );

      next();
    } catch (error) {
      console.error('权限检查错误:', error);
      return responseFormatter.internalError(res, '权限验证失败', error);
    }
  };
}

/**
 * 检查用户是否具有足够的角色级别
 * @param {string} minimumRole - 最低要求的角色
 * @returns {Function} Express中间件函数
 */
function requireMinimumRole(minimumRole) {
  return (req, res, next) => {
    try {
      // 检查用户是否已认证
      if (!req.user) {
        return responseFormatter.unauthorized(res, '用户未认证');
      }

      const userRole = req.user.role;
      const userLevel = ROLE_LEVELS[userRole] || 0;
      const requiredLevel = ROLE_LEVELS[minimumRole] || 0;

      if (userLevel < requiredLevel) {
        return responseFormatter.forbidden(res, `需要至少 ${minimumRole} 角色权限`);
      }

      // 记录权限检查日志
      console.log(
        `[${new Date().toISOString()}] Role Level Check: User ${req.user.username} (${userRole}:${userLevel}) meets minimum requirement (${minimumRole}:${requiredLevel})`
      );

      next();
    } catch (error) {
      console.error('角色级别检查错误:', error);
      return responseFormatter.internalError(res, '权限验证失败', error);
    }
  };
}

/**
 * 检查用户是否为资源所有者或具有管理权限
 * @param {Function} getResourceOwnerId - 获取资源所有者ID的函数
 * @returns {Function} Express中间件函数
 */
function requireOwnershipOrAdmin(getResourceOwnerId) {
  return async (req, res, next) => {
    try {
      // 检查用户是否已认证
      if (!req.user) {
        return responseFormatter.unauthorized(res, '用户未认证');
      }

      const userRole = req.user.role;
      const {userId} = req.user;

      // 管理员可以访问所有资源
      if (userRole === 'admin') {
        return next();
      }

      // 获取资源所有者ID
      const resourceOwnerId = await getResourceOwnerId(req);

      // 检查用户是否为资源所有者
      if (userId !== resourceOwnerId) {
        return responseFormatter.forbidden(res, '只能访问自己的资源');
      }

      // 记录权限检查日志
      console.log(
        `[${new Date().toISOString()}] Ownership Check: User ${req.user.username} accessing own resource`
      );

      next();
    } catch (error) {
      console.error('所有权检查错误:', error);
      return responseFormatter.internalError(res, '权限验证失败', error);
    }
  };
}

/**
 * 条件权限检查
 * @param {Function} condition - 条件检查函数
 * @param {Array|string} allowedRoles - 满足条件时允许的角色
 * @param {Array|string} fallbackRoles - 不满足条件时允许的角色
 * @returns {Function} Express中间件函数
 */
function conditionalRole(condition, allowedRoles, fallbackRoles = []) {
  return async (req, res, next) => {
    try {
      // 检查用户是否已认证
      if (!req.user) {
        return responseFormatter.unauthorized(res, '用户未认证');
      }

      const userRole = req.user.role;
      const conditionMet = await condition(req);

      const targetRoles = conditionMet
        ? Array.isArray(allowedRoles)
          ? allowedRoles
          : [allowedRoles]
        : Array.isArray(fallbackRoles)
          ? fallbackRoles
          : [fallbackRoles];

      if (!targetRoles.includes(userRole)) {
        return responseFormatter.forbidden(
          res,
          `当前条件下需要以下角色之一: ${targetRoles.join(', ')}`
        );
      }

      // 记录权限检查日志
      console.log(
        `[${new Date().toISOString()}] Conditional Role Check: User ${req.user.username} (${userRole}) - condition: ${conditionMet}`
      );

      next();
    } catch (error) {
      console.error('条件权限检查错误:', error);
      return responseFormatter.internalError(res, '权限验证失败', error);
    }
  };
}

/**
 * 获取用户权限列表
 * @param {string} role - 用户角色
 * @returns {Array} 权限列表
 */
function getUserPermissions(role) {
  return ROLE_PERMISSIONS[role] || [];
}

/**
 * 检查角色是否具有指定权限
 * @param {string} role - 角色
 * @param {string} permission - 权限
 * @returns {boolean} 是否具有权限
 */
function hasPermission(role, permission) {
  const permissions = ROLE_PERMISSIONS[role] || [];
  return permissions.includes(permission);
}

/**
 * 获取角色级别
 * @param {string} role - 角色
 * @returns {number} 角色级别
 */
function getRoleLevel(role) {
  return ROLE_LEVELS[role] || 0;
}

/**
 * 比较两个角色的级别
 * @param {string} role1 - 角色1
 * @param {string} role2 - 角色2
 * @returns {number} 比较结果 (1: role1 > role2, 0: 相等, -1: role1 < role2)
 */
function compareRoles(role1, role2) {
  const level1 = getRoleLevel(role1);
  const level2 = getRoleLevel(role2);

  if (level1 > level2) {return 1;}
  if (level1 < level2) {return -1;}
  return 0;
}

/**
 * 权限检查中间件工厂
 * 根据HTTP方法自动选择权限检查策略
 * @param {string} resource - 资源名称
 * @returns {Function} Express中间件函数
 */
function autoPermissionCheck(resource) {
  return (req, res, next) => {
    const method = req.method.toLowerCase();
    let requiredPermission;

    switch (method) {
      case 'get':
        requiredPermission = `${resource}:read`;
        break;
      case 'post':
        requiredPermission = `${resource}:create`;
        break;
      case 'put':
      case 'patch':
        requiredPermission = `${resource}:update`;
        break;
      case 'delete':
        requiredPermission = `${resource}:delete`;
        break;
      default:
        return responseFormatter.error(res, '不支持的HTTP方法', 405);
    }

    return requirePermission(requiredPermission)(req, res, next);
  };
}

module.exports = {
  requireRole,
  requirePermission,
  requireMinimumRole,
  requireOwnershipOrAdmin,
  conditionalRole,
  autoPermissionCheck,
  getUserPermissions,
  hasPermission,
  getRoleLevel,
  compareRoles,
  ROLE_LEVELS,
  ROLE_PERMISSIONS
};

/**
 * 输入验证中间件
 * 使用Joi进行请求数据验证和清理
 */

import Joi from 'joi';
import { ValidationError } from '../utils/errorHandler.js';

/**
 * 通用验证中间件
 * @param {Object} schema - Joi验证模式
 * @param {Object} options - 验证选项
 */
export const validateRequest = (schema, options = {}) => {
  const defaultOptions = {
    abortEarly: false, // 返回所有验证错误
    allowUnknown: false, // 不允许未知字段
    stripUnknown: true, // 移除未知字段
    ...options
  };

  return (req, res, next) => {
    const dataToValidate = {
      body: req.body || {},
      query: req.query || {},
      params: req.params || {},
      headers: req.headers || {}
    };

    const { error, value } = schema.validate(dataToValidate, defaultOptions);

    if (error) {
      const errorMessage = error.details.map((detail) => detail.message).join('; ');

      const field = error.details[0]?.path?.join('.') || null;

      return next(new ValidationError(errorMessage, field));
    }

    // 将验证后的数据替换原始数据
    req.body = value.body || {};
    req.query = value.query || {};
    req.params = value.params || {};

    next();
  };
};

/**
 * 自定义验证规则
 */
const customValidators = {
  // 设备ID验证
  deviceId: Joi.string()
    .pattern(/^[a-zA-Z0-9_-]+$/)
    .min(3)
    .max(50)
    .messages({
      'string.pattern.base': '设备ID只能包含字母、数字、下划线和连字符',
      'string.min': '设备ID长度至少3个字符',
      'string.max': '设备ID长度不能超过50个字符'
    }),

  // 时间戳验证
  timestamp: Joi.date().iso().max('now').messages({
    'date.format': '时间戳必须是有效的ISO 8601格式',
    'date.max': '时间戳不能是未来时间'
  }),

  // 能耗值验证
  energyValue: Joi.number().positive().precision(3).max(999999).messages({
    'number.positive': '能耗值必须为正数',
    'number.precision': '能耗值最多保留3位小数',
    'number.max': '能耗值不能超过999999'
  }),

  // 碳排放值验证
  carbonValue: Joi.number().min(0).precision(6).max(999999).messages({
    'number.min': '碳排放值不能为负数',
    'number.precision': '碳排放值最多保留6位小数',
    'number.max': '碳排放值不能超过999999'
  }),

  // 分页参数验证
  pagination: {
    page: Joi.number().integer().min(1).default(1),
    limit: Joi.number().integer().min(1).max(100).default(20),
    offset: Joi.number().integer().min(0)
  },

  // 时间范围验证
  timeRange: {
    startTime: Joi.date().iso().required(),
    endTime: Joi.date().iso().min(Joi.ref('startTime')).required()
  },

  // 用户名验证
  username: Joi.string().alphanum().min(3).max(30).messages({
    'string.alphanum': '用户名只能包含字母和数字',
    'string.min': '用户名长度至少3个字符',
    'string.max': '用户名长度不能超过30个字符'
  }),

  // 密码验证
  password: Joi.string()
    .min(8)
    .max(128)
    .pattern(/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]$/)
    .messages({
      'string.min': '密码长度至少8个字符',
      'string.max': '密码长度不能超过128个字符',
      'string.pattern.base': '密码必须包含大小写字母、数字和特殊字符'
    }),

  // 邮箱验证
  email: Joi.string().email().max(255).messages({
    'string.email': '邮箱格式无效',
    'string.max': '邮箱长度不能超过255个字符'
  })
};

/**
 * 能源数据验证模式
 */
export const energyDataSchema = Joi.object({
  body: Joi.object({
    device_id: customValidators.deviceId.required(),
    value: customValidators.energyValue.required(),
    unit: Joi.string().valid('kWh', 'MWh', 'GWh').required(),
    timestamp: customValidators.timestamp.required(),
    location: Joi.string().max(100).optional(),
    metadata: Joi.object().optional()
  })
});

/**
 * 碳排放数据验证模式
 */
export const carbonDataSchema = Joi.object({
  body: Joi.object({
    device_id: customValidators.deviceId.required(),
    emission_value: customValidators.carbonValue.required(),
    emission_factor: Joi.number().positive().precision(6).required(),
    energy_consumption: customValidators.energyValue.required(),
    timestamp: customValidators.timestamp.required(),
    calculation_method: Joi.string().valid('direct', 'indirect', 'lifecycle').default('direct'),
    metadata: Joi.object().optional()
  })
});

/**
 * 电池数据验证模式
 */
export const batteryDataSchema = Joi.object({
  body: Joi.object({
    device_id: customValidators.deviceId.required(),
