const router = express.Router();

// 应用响应格式化中间件
router.use(responseFormatter);

// API根路径信息
router.get('/', (req, res) => {
  res.success({
    message: '零碳园区数字孪生能碳管理系统 API',
    version: '2.0.0',
    status: 'running',
    timestamp: new Date().toISOString(),
    endpoints: {
