  // 邮箱验证
  email: body('email').isEmail().normalizeEmail().withMessage('邮箱格式无效'),

  // 手机号验证
  phone: body('phone').optional().matches(REGEX_PATTERNS.PHONE).withMessage('手机号格式无效'),

  // 角色验证
  role: body('role')
