// 验证器类
export class InputValidator {
  // 验证数据
  static async validate(data, schema, options = {}) {
    const defaultOptions = {
      abortEarly: false,
      allowUnknown: false,
      stripUnknown: true,
      convert: true
    };

    const validationOptions = { ...defaultOptions, ...options };

    try {
      const { error, value } = schema.validate(data, validationOptions);

      if (error) {
        const validationErrors = error.details.map((detail) => ({
          field: detail.path.join('.'),
          message: detail.message,
          value: detail.context?.value,
          type: detail.type
        }));

        logger.warn('数据验证失败', {
          errors: validationErrors,
          data: this.sanitizeLogData(data)
        });

        throw new ValidationError('数据验证失败', validationErrors);
      }

      return value;
    } catch (error) {
      if (error instanceof ValidationError) {
        throw error;
      }

      logger.error('验证过程中发生错误', {
        error: error.message,
        data: this.sanitizeLogData(data)
      });

      throw new ValidationError('验证过程中发生错误', null, null);
    }
  }

  // 验证单个字段
  static validateField(value, rule, fieldName = 'field') {
    try {
      const { error, value: validatedValue } = rule.validate(value);

      if (error) {
        throw new ValidationError(
          `${fieldName}验证失败: ${error.details[0].message}`,
          error.details,
          fieldName
        );
      }

      return validatedValue;
    } catch (error) {
      if (error instanceof ValidationError) {
        throw error;
      }

      throw new ValidationError(`${fieldName}验证过程中发生错误`, null, fieldName);
    }
  }

  // 清理敏感数据用于日志记录
  static sanitizeLogData(data) {
    if (!data || typeof data !== 'object') {
      return data;
    }

    const sensitiveFields = ['password', 'password_hash', 'token', 'secret', 'key'];
    const sanitized = { ...data };

    sensitiveFields.forEach((field) => {
      if (sanitized[field]) {
        sanitized[field] = '[REDACTED]';
      }
    });

    return sanitized;
  }

  // 验证邮箱格式
  static isValidEmail(email) {
    return validator.isEmail(email);
  }

  // 验证URL格式
  static isValidURL(url) {
    return validator.isURL(url);
  }

  // 验证IP地址
  static isValidIP(ip) {
    return validator.isIP(ip);
  }

  // 验证JSON字符串
  static isValidJSON(str) {
    try {
      JSON.parse(str);
      return true;
    } catch {
      return false;
    }
  }

  // 验证密码强度
  static validatePasswordStrength(password) {
    const checks = {
      length: password.length >= 8,
      lowercase: /[a-z]/.test(password),
      uppercase: /[A-Z]/.test(password),
      number: /\d/.test(password),
      special: /[@$!%*?&]/.test(password)
    };

    const score = Object.values(checks).filter(Boolean).length;

    return {
      score,
      strength: score < 3 ? 'weak' : score < 5 ? 'medium' : 'strong',
      checks
    };
  }

  // 清理HTML内容
  static sanitizeHTML(html) {
    return validator.escape(html);
  }

  // 清理SQL注入
  static sanitizeSQL(input) {
    if (typeof input !== 'string') {
      return input;
    }

    // 移除潜在的SQL注入字符
    return input.replace(/[';"\\]/g, '');
  }

  // 验证文件上传
  static validateFileUpload(file, options = {}) {
    const {
      maxSize = 10 * 1024 * 1024, // 10MB
      allowedTypes = ['image/jpeg', 'image/png', 'image/gif'],
      allowedExtensions = ['.jpg', '.jpeg', '.png', '.gif']
    } = options;

    const errors = [];

    if (!file) {
      errors.push('文件是必需的');
      return { valid: false, errors };
    }

    if (file.size > maxSize) {
      errors.push(`文件大小不能超过 ${maxSize / 1024 / 1024}MB`);
    }

    if (!allowedTypes.includes(file.mimetype)) {
      errors.push(`文件类型不支持，支持的类型: ${allowedTypes.join(', ')}`);
    }

    const fileExtension = file.originalname
      .toLowerCase()
      .substring(file.originalname.lastIndexOf('.'));
    if (!allowedExtensions.includes(fileExtension)) {
      errors.push(`文件扩展名不支持，支持的扩展名: ${allowedExtensions.join(', ')}`);
    }

    return {
      valid: errors.length === 0,
      errors
    };
  }
}

// Express中间件：请求验证
export function validationMiddleware(schema, source = 'body') {
  return async (req, res, next) => {
    try {
      const dataToValidate = req[source];
      const validatedData = await InputValidator.validate(dataToValidate, schema);

      // 将验证后的数据替换原始数据
      req[source] = validatedData;

      next();
    } catch (error) {
      if (error instanceof ValidationError) {
        return res.apiValidationError(error.details, error.message);
      }

      logger.error('验证中间件错误', { error: error.message });
      return res.apiSystemError('验证过程中发生错误');
    }
  };
}

// 查询参数验证中间件
export function queryValidationMiddleware(schema) {
  return validationMiddleware(schema, 'query');
}

// 路径参数验证中间件
export function paramsValidationMiddleware(schema) {
  return validationMiddleware(schema, 'params');
}

// 导出常用验证函数
export const { validate } = InputValidator;
export const { validateField } = InputValidator;

/**
 * 结构化日志模块
 * 使用Winston实现分级日志记录和管理
 */

import winston from 'winston';
import path from 'path';
import fs from 'fs';
// import { fileURLToPath } from 'url'; // Not needed after removing __filename
import config from '../config/index.js';

// 定义审计日志的格式
const auditLogFormat = winston.format.combine(
  winston.format.timestamp({ format: 'YYYY-MM-DD HH:mm:ss.SSS' }),
  winston.format.json(),
  winston.format.printf(
    ({ timestamp, level, message, userId, ipAddress, action, target, details }) => {
      const logEntry = {
        timestamp,
        level: level.toUpperCase(),
        message,
        userId: userId || 'N/A',
        ipAddress: ipAddress || 'N/A',
        action: action || 'N/A',
        target: target || 'N/A',
        details: details || {}
      };
      return JSON.stringify(logEntry);
    }
  )
);

// const __filename = fileURLToPath(import.meta.url); // Not directly used after removing __dirname
// const currentDir = path.dirname(__filename); // __dirname is not used directly, but path is used with config.logging.logDir

/**
 * 确保日志目录存在
 */
function ensureLogDirectory() {
  const { logDir } = config.logging;
  if (!fs.existsSync(logDir)) {
    fs.mkdirSync(logDir, { recursive: true });
  }
}

/**
 * 自定义日志格式
 */
const customFormat = winston.format.combine(
  winston.format.timestamp({
    format: 'YYYY-MM-DD HH:mm:ss.SSS'
  }),
  winston.format.errors({ stack: true }),
  winston.format.json(),
  winston.format.printf(({ timestamp, level, message, service, requestId, userId, ...meta }) => {
    const logEntry = {
      timestamp,
      level: level.toUpperCase(),
      message,
      service: service || 'zero-carbon-park',
      ...(requestId && { requestId }),
      ...(userId && { userId }),
      ...meta
    };

    return JSON.stringify(logEntry);
  })
);

/**
 * 控制台格式（开发环境友好）
 */
const consoleFormat = winston.format.combine(
  winston.format.timestamp({
    format: 'HH:mm:ss.SSS'
  }),
  winston.format.colorize(),
  winston.format.printf(({ timestamp, level, message, service, requestId, userId, ...meta }) => {
    let logMessage = `${timestamp} [${level}]`;

    if (service) {
      logMessage += ` [${service}]`;
    }

    if (requestId) {
      logMessage += ` [${requestId}]`;
    }

    if (userId) {
      logMessage += ` [User:${userId}]`;
    }

    logMessage += `: ${message}`;

    // 添加额外的元数据
    if (Object.keys(meta).length > 0) {
      logMessage += ` ${JSON.stringify(meta)}`;
    }

    return logMessage;
  })
);

/**
 * 创建传输器配置
 */
function createTransports() {
  const transports = [];

  // 控制台传输器
  if (config.logging.enableConsole) {
    transports.push(
      new winston.transports.Console({
        level: config.logging.level,
        format: config.app.env === 'development' ? consoleFormat : customFormat,
        handleExceptions: true,
        handleRejections: true
      })
    );
  }

  // 文件传输器
  if (config.logging.enableFile) {
    transports.push(
      new winston.transports.File({
        filename: path.join(config.logging.logDir, 'app.log'),
        level: config.logging.level,
        format: customFormat,
        maxsize: config.logging.maxFileSize,
        maxFiles: config.logging.maxFiles,
        tailable: true
      })
    );
  }

  // 错误日志文件传输器
  if (config.logging.enableErrorFile) {
    transports.push(
      new winston.transports.File({
        filename: path.join(config.logging.logDir, 'error.log'),
        level: 'error',
        format: customFormat,
        maxsize: config.logging.maxFileSize,
        maxFiles: config.logging.maxFiles,
        tailable: true
      })
    );
  }

  return transports;
}

ensureLogDirectory();

/**
 * 主日志器实例
 */
const logger = winston.createLogger({
  levels: winston.config.npm.levels,
  format: customFormat,
  transports: createTransports(),
  exitOnError: false // 不在异常时退出应用
});

/**
 * 审计日志器实例
 */
export const auditLogger = winston.createLogger({
  levels: winston.config.npm.levels, // 可以根据需要定义更细粒度的审计日志级别
  format: auditLogFormat,
  transports: [
    new winston.transports.File({
      filename: path.join(config.logging.logDir, 'audit.log'),
      level: 'info', // 审计日志通常从info级别开始记录
      format: auditLogFormat,
      maxsize: config.logging.maxFileSize,
      maxFiles: config.logging.maxFiles,
      tailable: true
    }),
    ...(config.logging.enableConsole
      ? [
        new winston.transports.Console({
          level: 'info',
          format: consoleFormat
        })
      ]
      : [])
  ],
  exitOnError: false
});

// 如果是开发环境，将日志输出到控制台
if (config.app.env === 'development') {
  logger.add(
    new winston.transports.Console({
      format: winston.format.combine(winston.format.colorize(), winston.format.simple()),
      level: 'debug'
    })
  );
}

/**
 * 日志器包装类
 * 提供更丰富的日志记录功能
 */
class Logger {
  constructor(service = 'default') {
    this.service = service;
    this.defaultMeta = { service };
  }

  /**
   * 创建子日志器
   * @param {string} service - 服务名称
   * @returns {Logger} 子日志器实例
   */
  child(service) {
    return new Logger(`${this.service}:${service}`);
  }

  /**
   * 记录错误日志
   * @param {string} message - 日志消息
   * @param {Object} meta - 额外元数据
   */
  error(message, meta = {}) {
    logger.error(message, { ...this.defaultMeta, ...meta });
  }

  /**
   * 记录警告日志
   * @param {string} message - 日志消息
   * @param {Object} meta - 额外元数据
   */
  warn(message, meta = {}) {
    logger.warn(message, { ...this.defaultMeta, ...meta });
  }

  /**
   * 记录信息日志
   * @param {string} message - 日志消息
   * @param {Object} meta - 额外元数据
   */
  info(message, meta = {}) {
    logger.info(message, { ...this.defaultMeta, ...meta });
  }

  /**
   * 记录HTTP日志
   * @param {string} message - 日志消息
   * @param {Object} meta - 额外元数据
   */
  http(message, meta = {}) {
    logger.http(message, { ...this.defaultMeta, ...meta });
  }

  /**
   * 记录调试日志
   * @param {string} message - 日志消息
   * @param {Object} meta - 额外元数据
   */
  debug(message, meta = {}) {
    logger.debug(message, { ...this.defaultMeta, ...meta });
  }

  /**
   * 记录数据库操作日志
   * @param {string} operation - 操作类型
   * @param {string} table - 表名
   * @param {Object} meta - 额外元数据
   */
  database(operation, table, meta = {}) {
    this.debug(`数据库操作: ${operation}`, {
      operation,
      table,
      ...meta
    });
  }

  /**
   * 记录API请求日志
   * @param {Object} req - Express请求对象
   * @param {Object} res - Express响应对象
   * @param {number} duration - 请求处理时间（毫秒）
   */
  apiRequest(req, res, duration) {
    const meta = {
      method: req.method,
      url: req.originalUrl,
      statusCode: res.statusCode,
      duration: `${duration}ms`,
      userAgent: req.get('User-Agent'),
      ip: req.ip || req.connection.remoteAddress,
      requestId: req.headers['x-request-id'],
      userId: req.user?.id
    };

    const message = `${req.method} ${req.originalUrl} ${res.statusCode} - ${duration}ms`;

    if (res.statusCode >= 400) {
      this.warn(message, meta);
    } else {
      this.http(message, meta);
    }
  }

  /**
   * 记录MQTT消息日志
   * @param {string} topic - MQTT主题
   * @param {string} action - 操作类型（publish/subscribe/receive）
   * @param {Object} meta - 额外元数据
   */
  mqtt(topic, action, meta = {}) {
    this.debug(`MQTT ${action}: ${topic}`, {
      topic,
      action,
      ...meta
    });
  }

  /**
   * 记录性能指标日志
   * @param {string} metric - 指标名称
   * @param {number} value - 指标值
   * @param {string} unit - 单位
   * @param {Object} meta - 额外元数据
   */
  metric(metric, value, unit = '', meta = {}) {
    this.info(`性能指标: ${metric} = ${value}${unit}`, {
      metric,
      value,
      unit,
      ...meta
    });
  }

  /**
   * 记录安全事件日志
   * @param {string} event - 事件类型
   * @param {string} description - 事件描述
   * @param {Object} meta - 额外元数据
   */
  security(event, description, meta = {}) {
    this.warn(`安全事件: ${event} - ${description}`, {
      securityEvent: event,
      description,
      ...meta
    });
  }

  /**
   * 记录业务事件日志
   * @param {string} event - 事件类型
   * @param {string} description - 事件描述
   * @param {Object} meta - 额外元数据
   */
  business(event, description, meta = {}) {
    this.info(`业务事件: ${event} - ${description}`, {
      businessEvent: event,
      description,
      ...meta
    });
  }
}

/**
 * 请求日志中间件
 * @param {Object} options - 配置选项
 * @returns {Function} Express中间件函数
 */
export const requestLogger = () => {
  const requestLog = new Logger('http');

  return (req, res, next) => {
    if (!config.logging.enableRequestLogging) {
      return next();
    }

    const startTime = Date.now();

    // 生成请求ID
    req.requestId =
      req.headers['x-request-id'] || `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    // 设置响应头
    res.setHeader('X-Request-ID', req.requestId);

    // 记录请求开始
    requestLog.http(`请求开始: ${req.method} ${req.originalUrl}`, {
      requestId: req.requestId,
      method: req.method,
      url: req.originalUrl,
      userAgent: req.get('User-Agent'),
      ip: req.ip || req.connection.remoteAddress,
      userId: req.user?.id
    });

    // 监听响应结束事件
    res.on('finish', () => {
      const duration = Date.now() - startTime;
      requestLog.apiRequest(req, res, duration);
    });

    next();
