/**
 * 获取日志统计信息
 * @returns {Object} 统计信息
 */
export function getLogStats() {
  const { logDir } = config.logging;
  const stats = {
    logDirectory: logDir,
    files: []
  };

  if (fs.existsSync(logDir)) {
    const files = fs.readdirSync(logDir);

    for (const file of files) {
      if (file.endsWith('.log')) {
        const filePath = path.join(logDir, file);
        const fileStat = fs.statSync(filePath);

        stats.files.push({
          name: file,
          size: fileStat.size,
          modified: fileStat.mtime,
          created: fileStat.birthtime
        });
      }
    }
  }

  return stats;
}

/**
 * 清理旧日志文件
 * @param {number} daysToKeep - 保留天数
 */
export function cleanupOldLogs(daysToKeep = 30) {
  const { logDir } = config.logging;
  const cleanupLog = new Logger('cleanup');

  if (!fs.existsSync(logDir)) {
    return;
  }

  const cutoffDate = new Date();
  cutoffDate.setDate(cutoffDate.getDate() - daysToKeep);

  const files = fs.readdirSync(logDir);
  let deletedCount = 0;

  for (const file of files) {
    if (file.endsWith('.log')) {
      const filePath = path.join(logDir, file);
      const fileStat = fs.statSync(filePath);

      if (fileStat.mtime < cutoffDate) {
        try {
          fs.unlinkSync(filePath);
          deletedCount++;
          cleanupLog.info(`删除旧日志文件: ${file}`);
        } catch (error) {
          cleanupLog.error(`删除日志文件失败: ${file}`, { error: error.message });
        }
      }
    }
  }

  cleanupLog.info(`日志清理完成，删除了 ${deletedCount} 个文件`);
}

